.echo OFF

drop table if exists ramban;
drop table if exists rambanOnRambam;

create table ramban (
	_id integer NOT NULL primary key,
	mitzvahId integer NOT NULL,
	mitzvahNumber integer NOT NULL,
	shihchaNumber integer,
	source integer,
	originalText varchar,
	foreign key (mitzvahId) references mitzvos(_id) on delete cascade,
	foreign key (source) references verses(_id) on delete cascade
);

--insert into ramban values (97, 625, 97, 6,  272, 'נצטוינו כשנצור על עיר לאכול מן האילנות שבגבולה כל ימי המצור ואם נכרות אותם לבטלה דרך השחתה נעבור על עשה מוסיף על הלאו המפורש בו והוא אמרו יתעלה כי ממנו תאכל מ"ע ואותו לא תכרות מצות ל"ת, וראוי הרב לפי חשבונו במצות שימנה גם את זה. ודע כי זו המצוה בעשה ולא תעשה שלה כשנצור על עיר להלחם עליה לתפשה שנצטוינו לחמול עליה כאשר נחמול על שלנו אולי נכבוש אותה, אבל בצאתנו מעל ארץ אויב נשחית ונחבל כל עץ טוב וכן בימי המצור להצר לאנשי העיר בהשחתת האילנות שלא יחיו מהם כל זה מותר, לא אסרה תורה אלא השחתה בחנם, ולשון ספרי לבוא מפניך במצור קצצהו, ולשון הרב במצות ל"ת נ"ז איננו מכוון:');


-- Ramban's comments on the mitzvos of the Rambam
create table rambanOnRambam (
	_id integer NOT NULL primary key,
	originalText varchar,
	foreign key (_id) references rambam(_id) on delete cascade
);

