  9  - Miseder Nashim
  10 - Hechsher Maachalos
  11 - Nezikin
  12 - Nedarim UShevuos
  13 - Mikdash
*/
create table semag (
	_id integer NOT NULL primary key,
	mitzvahId integer NOT NULL UNIQUE,
	mitzvahNumber integer NOT NULL,
	category integer,
  semagTitle varchar,
	source integer,
	foreign key (mitzvahId) references mitzvos(_id) on delete cascade,
	foreign key (source) references verses(_id) on delete cascade
);

