insert into books values (1, 'בראשית', 'Genesis');
insert into books values (2, 'שמות', 'Exodus');
insert into books values (3, 'ויקרא', 'Leviticus');
insert into books values (4, 'במדבר', 'Numbers');
insert into books values (5, 'דברים', 'Deuteronomy');
insert into verses values (199, 1, 9,  7,  'ואתם פרו ורבו שרצו בארץ ורבו-בה', "And now be fruitful and multiply, spread throughout the earth and multiply on it.");
insert into verses values (202, 1, 17, 13, 'המול ימול יליד ביתך ומקנת כספך והיתה בריתי בבשרכם לברית עולם', "Your house-born slaves must be circumcised, and also those bought with your money. This shall be My covenant in your flesh as an eternal covenant.");
insert into verses values (359, 1, 32, 33, 'על-כן לא-יאכלו בני-ישראל את-גיד הנשה אשר על-כף הירך עד היום הזה כי נגע בכף-ירך יעקב בגיד הנשה', "Therefore, the children of Israel must not eat the displaced tendon [nerve] which is on the hip joint to this very day; because he struck Yaakov’s hip joint on the displaced tendon.");
insert into verses values (142, 2, 12, 2,  'החדש הזה לכם ראש חדשים ראשון הוא לכם לחדשי השנה', "“This month shall be [reckoned] to you [as] the head [beginning] of months. It shall be to you the first of the months of the year.");
insert into verses values (52,  2, 12, 6,  'והיה לכם למשמרת עד ארבעה עשר יום לחדש הזה ושחטו אתו כל קהל עדת-ישראל בין הערבים', "You shall hold it in safekeeping until the fourteenth day of this month, they shall slaughter it—the entire community of Yisrael—between evenings [in the afternoon].");
insert into verses values (53,  2, 12, 8,  'ואכלו את-הבשר בלילה הזה צלי-אש ומצות על-מררים יאכלהו', "They shall eat the meat during this night. It shall be roasted over fire. They shall eat it with matzos and bitter herbs. ");
insert into verses values (321, 2, 12, 9,  'אל-תאכלו ממנו נא ובשל מבשל במים כי אם-צלי-אש ראשו על-כרעיו ועל-קרבו', "You must not eat it half-cooked or boiled in water, but only roasted over fire, its head with its knees and its inner organs.");
insert into verses values (315, 2, 12, 10, 'ולא-תותירו ממנו עד-בקר והנתר ממנו עד-בקר באש תשרפו', "You must not leave any of it over until morning. Any of it left over until morning must be burned in fire.");
insert into verses values (145, 2, 12, 15, 'שבעת ימים מצות לאכלו אך ביום הראשון תשביתו שער מבתיכם כי כל-אכל חמץ ונכרתה הנפש ההוא מישראל מיום הראשון עד-יום השבעי', "You must eat matzos for seven days, but before the first day you must remove [all] leaven from your homes; for anyone who eats chametz, that soul will be cut off from Yisrael. [Chametz is forbidden] from the first day [of Pesach] until [after] the seventh day.");
insert into verses values (148, 2, 12, 16, 'וביום הראשון מקרא-קדש וביום השביעי מקרא-קדש יהיה לכם כל-מלאכה לא-יעשה בהם אך אשר יאכל לכל-נפש הוא לבדו יעשה לכם', "The first day shall be a holy assembly and the seventh day shall be a holy assembly to you. No work shall be done on them, only for [the preparation of food] which will be eaten by every person, that alone may be done for you.");
insert into verses values (147, 2, 12, 18, 'בראשן בארבעה עשר יום לחדש בערב חאכלו מצת עד יום האחד ועשרים לחדש בערב', "In the first [month] on the fourteenth day of the month, in the evening you shall eat matzos; until the twenty-first day of the month in the evening.");
insert into verses values (374, 2, 12, 19, 'שבעת ימים שאר לא ימצא בבתיכם כי כל-אכל מחמצת ונכרתה הנפש ההוא מעדת ישראל בגר ובאזרח הארץ', "For seven days leaven may not be found in your homes, for whoever eats chametz that soul shall be cut off from the community of Israel, whether a proselyte or a native born in the land.");
insert into verses values (371, 2, 12, 20, 'כל-מחמצת לא תאכלו בכל מושבתיכם מאכלו מצות', "You must not eat anything that is chametz. In all your dwellings you shall eat matzos.");
insert into verses values (324, 2, 12, 43, 'ויאמר יקוק אל-משה ואהרן זאת חקת הפסח כל-בן-נכר לא-יאכל בו', "Adonoy said to Moshe and Aharon, “This is the statute of the Pesach offering: No alien may eat it.");
insert into verses values (322, 2, 12, 45, 'תושב ושכיר לא-יאכל-בו', "An [alien] sojourner or hired worker may not eat it.");
insert into verses values (319, 2, 12, 46, 'בבית אחד יאכל לא-תוציא מן-הבית מן-הבשר חוצה ועצם לא תשברו-בו', "It must be eaten in one house. You must not remove from the house any of its flesh, to the outdoors, and you must not break any of its bones.");
insert into verses values (323, 2, 12, 48, 'וכי-יגור אתך גר ועשה פסח ליקוק המול לו כל-זכר ואז יקרב לעשתו והיה כאזרח הארץ וכל-ערל לא-יאכל בו', "When a proselyte dwells with you and wants to make the Pesach-offering to Adonoy, every male must be circumcised. He may then come near [join] to make it, and be like the native born. But no uncircumcised male may eat of it.");
insert into verses values (75,  2, 13, 2,  'קדש-לי כל-בכור פטר כל-רחם בבני ישראל באדם ובבהמה לי הוא', "“Sanctify to Me all the first-born, that [is the first to] open the womb among the B’nei Yisrael, both of man and beast, it is Mine.”");
insert into verses values (370, 2, 13, 3,  'ויאמר משה אל-העם זכור את-היום הזה אשר יצאתם ממצרים מבית עבדים כי בחזק יד הוציא יקוק אתכם מזה ולא יכאל חמץ', "Moshe said to the people, “Remember this day [as the day] on which you came out of Egypt, from the house of slavery, for with a strong hand Adonoy brought you out from this. You must not eat chametz.");
insert into verses values (373, 2, 13, 7,  'מצות יאכל את שבעת הימים ולא-יראה לך חמץ ולא-יראה לך שאר בכל-גבלך', "Matzos must be eaten these seven days. No chametz may be seen in your possession, and no leaven may be seen in all your boundaries.");
insert into verses values (146, 2, 13, 8,  'והגדת לבנך ביום ההוא לאמר בעבור זה עשה יקוק לי בצאתי ממצרים', "You must tell your son [child] on that day saying, ‘Because of this, Adonoy did [this] for me when I came out of Egypt.’");
insert into verses values (78,  2, 13, 13, 'וכל-פטר חמר תפדה בשה ואם-לא תפדה וערפתו וכל בכור אדם בניך תפדה', "Redeem each firstling donkey with a sheep. If it is not redeemed, you must break its neck. Redeem every first-born male among your sons.");
insert into verses values (445, 2, 16, 29, 'ראו כי-יקוק נתן לכם השבת על-כן הוא נתן לכם ביום הששי לחם יומים שבו איש תחתיו אל-יצא איש ממקמו ביום השביעי', "See, because Adonoy gave you the Shabbos, therefore He gave you on the sixth day bread for two days. Every man must remain in his place. No man may go out of his place on the seventh day.");
insert into verses values (1,   2, 20, 2,  'אנכי יקוק אלקיך אשר הוצאתיך מארץ מצרים מבית עבדים', "I am Adonoy, your God, Who brought you out of the land of Egypt, from the house of slaves.");
insert into verses values (234, 2, 20, 3,  'לא יהיה-לך אלהים אחרים על-פני', "You must not have any other gods before My Presence.");
insert into verses values (235, 2, 20, 4,  'לא תעשה-לך פסל וכל-תמונה אשר בשמים ממעל ואשר בארץ מתחת ואשר במים מתחת לארץ', "You must not make for yourself any carved image, or picture of anything that is in the heaven above, or on the earth below, or in the water below the earth.");
insert into verses values (238, 2, 20, 5,  'לא-תשתחוה להם ולא תעבדם כי אנכי יקוק אלקיך קל קנא פקד עון אבת על-בנים על-שלשים ועל-רבעים לשנאי', "You must not prostrate yourself to them, and you must not worship them. For I, Adonoy your God, am a jealous Almighty. I am cognizant of the sins of fathers for [their] children, to the third and fourth [generations] of My enemies.");
insert into verses values (276, 2, 20, 7,  'לא תשא את-שם-יקוק אלקיך לשוא כי לא ינקה יקוק את אשר-ישא את-שמו לשוא', "Do not take the Name of Adonoy, your God in vain. For Adonoy will not acquit the one who takes His Name in vain.");
insert into verses values (144, 2, 20, 8,  'זכור את-יום השבת לקדשו', "Remember the Shabbos day to sanctify it.");
insert into verses values (444, 2, 20, 10, 'ויום השביעי שבת ליקוק אלקיך לא-תעשה כל-מלאכה אתה ובנך-ובתך עבדך ואמתך ובהמתך וגרך אשר בשעריך', "But the seventh day is Shabbos to Adonoy, your God. You must not do any manner of work—you, your son, your daughter, your slave, your maid, your animal, and the foreigner within your gates.");
insert into verses values (197, 2, 20, 12, 'בכד את-אביך ואת-אמך למען יראכון ימיך על האדמה אשר-יקוק אלקיך נתן לך', "Honor your father and your mother so that your days may be lengthened upon the land that Adonoy, your God, is giving you.");
insert into verses values (400, 2, 20, 13, 'לא תרצח לא תנאף לא נגנב לא-תענה רעך עד שקר', "Do not commit murder. Do not commit adultery. Do not steal [by kidnapping human beings]. Do not testify as a false witness against your neighbor.");
insert into verses values (414, 2, 20, 14, 'לא תחמד בית רעך לא-תחמד אשת רעך ועבדו ואמתו ושורו וחמרו וכל אשר לרעך', "Do not covet your neighbor’s house. Do not covet your neighbor’s wife, his slave, his maid, his ox, his donkey, or anything else that belongs to your neighbor.");
insert into verses values (237, 2, 20, 20, 'לא תעשון אתי אלהי כסף ואלהי זהב לא תעשו לכם', "Do not make[an image of anything that is] with Me, gods of silver or gods of gold you must not make for yourselves.");
insert into verses values (289, 2, 20, 22, 'ואם-מזבח אבנים תעשה-לי לא-תבנה אתהן גזית כי חרבך הנפת עליה ותחללה', "When you build a stone altar for Me, do not build it out of hewn stone, lest you lift your sword on it, you will [therewith] have profaned it.");
insert into verses values (290, 2, 20, 23, 'ולא-תעלה במעלת על-מזבחי אשר לא-תגלה ערותך עליו', "Do not go up to My altar with steps, so that your nakedness not be revealed on it.’”");
insert into verses values (218, 2, 21, 2,  'כי תקנה עבד עברי שש שנים יעבד ובשעית יצא לחפשי חנם', "If you buy a Hebrew slave, he shall serve [you] for six years, but in the seventh [year] he is to go out free, for naught [without liability].");
insert into verses values (219, 2, 21, 8,  'אם-רעה בעיני אדניה אשר-לא יעדה והפדה לעם נכרי לא-ימשל למכרה בבגדו-בה', "If she is displeasing to her master, who had not designated her as his wife, he must allow her to be redeemed. He has no power [right] to sell her to an alien people for he has dealt deceitfully with her.");
insert into verses values (412, 2, 21, 10, 'אם-אחרת יקח-לו שארה כסותה וענתה לא יגרע', "If he takes himself another [wife], her sustenance, her clothing and her conjugal rights he must not diminish.");
insert into verses values (443, 2, 21, 15, 'ומכה אביו ואמו מות יומת', "Whoever strikes his father or his mother shall be put to death.");
insert into verses values (442, 2, 21, 17, 'ומקלל אביו ואמו מות יומת', "Whoever curses his father or his mother shall be put to death.");
insert into verses values (221, 2, 21, 18, 'וכי-יריבן אנשים והנה-איש את-רעהו באבן או באגרף ולא ימות ונפל למשכב', "When men quarrel and one man hits his fellow with a stone or with [his] fist; and he [the victim] does not die but becomes bedridden,");
insert into verses values (212, 2, 21, 20, 'וכי-יכה איש את-עבדו או את-אמתו בשבט ומת תחת ידו נקם ינקם', "If a man strikes his male or female slave with a rod, and [the slave] dies under his hand, [the death] must be avenged.");
insert into verses values (491, 2, 21, 26, 'וכי-יכה איש את-עין עבדו או-את-עין אמתו ושחתה לחפשי ישלחנו תחת עינו', "If a man strikes the eye of his slave, or the eye of his female slave, and destroys [blinds] it, he shall set him [the slave] free as compensation for the eye.");
insert into verses values (363, 2, 21, 28, 'וכי-יגח שור את-איש או את-אשה ומת סקול יסקל השור ולא יאכל את-בשרו ובעל השור נקי', "If an ox gores a man or woman and the victim dies, the ox shall be stoned [to death], and its flesh may not be eaten. The owner of the ox shall go unpunished.");
insert into verses values (223, 2, 21, 33, 'וכי-יפתח איש בוד או כי-יכרה איש בר ולא יכסנו ונפל-שמה שור או חמור', "If a man uncovers a pit, or if a man digs a pit, and does not cover it, and an ox or a donkey falls into it;");
insert into verses values (222, 2, 21, 35, 'וכי-יקף שור-איש את-שור רעהו ומת ומכרו את-השור החי וחצו את-כספו וגם את-המת יחצון', "If a man’s ox injures his neighbor’s ox and it dies, they shall sell the live ox and divide the money received for it. They shall also divide [the value of] the dead animal.");
insert into verses values (224, 2, 21, 37, 'כי יגנב-איש שור או-שה וטבחו או מכרו חמשה בקר ישלם תחת השור וארבע-צאן תחת השה', "If a man steals an ox or a sheep, and then slaughters or sells it, he must pay five oxen for each ox, and four sheep for each sheep.");
insert into verses values (225, 2, 22, 4,  'כי יבער-איש שדה או-כרם ושלח את-בעירה ובער בשדה אחר מיטב שדהו ומיטב כרמו ישלם', "If he damages a field or vineyard with his animal—he lets his livestock loose, and [or] it feeds in another’s field—from his [own] prime field and prime vineyard he must compensate [the owner].");
insert into verses values (226, 2, 22, 5,  'כי-תצא אש ומצאה קצים ונאכל גדיש או הקמה או השדה שלם ישלם המבער את-הבערה', "If a fire goes out of control and finds [catches on] thorns, and then consumes bound or standing grain or a field, full restitution must be made by the one who started the fire.");
insert into verses values (227, 2, 22, 6,  'כי-יתן איש אל-רעהו כסף או-כלים לשמר וגנב מבית האיש אם-מצא הגנב ישלם שנים', "If a man gives his neighbor money or articles to safeguard and they are stolen from the house of the [watch]man, if the thief is found, he must pay double.");
insert into verses values (231, 2, 22, 8,  'על-כל-דבר-פשע על-שור על-חמור על-שה על-שלמה על-כל-אבדה אשר יאמר כי-הוא זה עד האלהים יבא דבר-שניהם אשר ירשיען אלהים ישלם שנים לרעהו', "In every question of dishonesty whether it involves an ox, a donkey, a sheep, a garment, or anything that was [allegedly] lost of which [a witness] says, “This is it!” the claims of both parties must be brought to the judges. The one whom the judges find guilty, must pay double restitution to his neighbor.");
insert into verses values (228, 2, 22, 9,  'כי-יתן איש אל-רעהו חמור או-שור או-שה וכל-בהמה לשמר ומת או-נשבר או-נשבה אין ראה', "If a man gives his neighbor, a donkey, an ox, a sheep, or any animal for safeguarding; and it dies, or is injured, or is captured, and there is no eyewitness,");
insert into verses values (229, 2, 22, 13, 'וכי-ישאל איש מעם רעהו ונשבר או-מת בעליו אין-עמו שלם ישלם', "If a man borrows something from his neighbor, and it becomes broken or dies; if the owner is not [involved] with him, he [the borrower] must make full restitution.");
insert into verses values (207, 2, 22, 15, 'וכי-יפתה איש בתולה אשר לא-ארשה ושכב עמה מהר ומהרנה לא לאשה', "If a man seduces a virgin who is not betrothed, and lies with her; he must give the dowry to acquire her as his wife.");
insert into verses values (440, 2, 22, 17, 'מכשפה לא תחיה', "You shall not allow a witch to live.");
insert into verses values (404, 2, 22, 20, 'וגר לא-תונה ולא תלחצנו כי-גרים הייתם בארץ מצרים', "You must not abuse or oppress a stranger, for you were strangers in the land of Egypt.");
insert into verses values (407, 2, 22, 21, 'כל-אלמנה ויתום לא תענון', "You must not mistreat any widow or orphan.");
insert into verses values (184, 2, 22, 24, 'אם-כסף תלוה את-עמי את-העני עמך לא-תהיה לו כנשה לא-תשימון עליו נשך', "When you lend money to My people, to the poor person who is with [among] you, do not act toward him as a [demanding] creditor. Do not place the burden of interest upon him.");
insert into verses values (274, 2, 22, 27, 'אלקים לא תקלל ונשיא בעמך לא תאר', "Do not curse judges. Do not curse a leader of your people.");
insert into verses values (76,  2, 22, 28, 'מלאתך ודמעך לא תאחר בכור בניך תתן-לי', "[Your offering of] the fullness [of your newly ripened produce] and of your terumah-offering’s you must not delay. The first-born of your sons, you shall give to Me.");
insert into verses values (357, 2, 22, 30, 'ואנשי-קדש תהיון לי ובשר בשדה טרפה לא תאכלו לבלב תשלכון אתו', "You shall be men of holiness to Me, and you must not eat flesh that was torn off in the field. Throw it to the dog.");
insert into verses values (425, 2, 23, 1,  'לא תשא שמע שוא אל-תשת ידך עם-רשע להית עד חמס', "Do not accept a false report. Do not join hands with a wicked man to be a corrupt [false] witness.");
insert into verses values (163, 2, 23, 2,  'לא-תהיה אחרי-רבים לרעת ולא-תענה על-רב לנטת אחרי רבים להטת', "Do not follow the majority to do evil. Do not respond with your opinion in a dispute [merely] to lean [toward one side]. It must be decided by the majority.");
insert into verses values (422, 2, 23, 3,  'ודל לא תהדר בריבו', "Do not show favor [even] to the poor in his dispute.");
insert into verses values (189, 2, 23, 5,  'כי-תראה חמור שנאך רבץ תחת משאו וחדלת מעזב לו עזב תעזב עמו', "If you see the donkey of your enemy lying under its burden, and you might not want to help him, [but you should] make every effort to help him.");
insert into verses values (423, 2, 23, 6,  'לא תטה משפט אבינך בריבו', "Do not bend justice [even] for your needy in his dispute.");
insert into verses values (428, 2, 23, 7,  'מדבר-שקר תרחק ונקי וצדיק אל-תהרג כי לא-אצדיק רשע', "Keep far away from anything false. Do not kill an innocent righteous man, for I will not acquit a wicked person.");
insert into verses values (420, 2, 23, 8,  'ושחד לא תקח כי השחד יעור פקחים ויסלף דברי צדיקים', "Do not accept bribery, for bribery blinds the clear-sighted, and perverts the words of justice.");
insert into verses values (125, 2, 23, 11, 'והשביעת תשמטנה ונטשתה ואכלו אביני עמך ויתרם תאכל חית השדה כן-תעשה לכרמך לזיתך', "But on the seventh [year] you must let it rest, and abandon it [by not eating its produce], and let the needy among your people eat it. What they leave over, the beasts of the field can eat. Do the same with your vineyard and your olive [trees].");
insert into verses values (143, 2, 23, 12, 'ששת ימים תעשה מעשיך וביום השביעי תשבת למען ינוח שורך וחמורך וינפש בן-אמתך והגר', "You may do your work six days, but on the seventh day you must cease; so that your ox and your donkey may rest, and the son of your female slave may be refreshed, and [also] the stranger.");
insert into verses values (244, 2, 23, 13, 'ובכל אשר-אמרתי אליכם תשמרו ושם אלקים אחרים לא תזכירו לא ישמע על-פיך', "Preserve everything that I have said to you. Do not mention the name of other gods. You must not cause it to be heard through your mouth.");
insert into verses values (49,  2, 23, 14, 'שלש רגלים תחג לי בשנה', "Three times a year you are to celebrate with Me.");
insert into verses values (337, 2, 23, 15, 'את-חג המצות תשמר שבעת ימים תאכל מצות כאשר צויתך למועד חדש האביב כי-בו יצאת ממצרים ולא-יראו פני-ריקם', "You must keep the festival of Matzos. You must eat matzos for seven days, as I have commanded you. [This festival must be celebrated] at the appointed time, during the month when the grain is ripened, for in it [at this time] you went out of Egypt. Do not appear before My Presence empty-handed.");
insert into verses values (50,  2, 23, 17, 'שלש פעמים בשנה יראה כ-זכורך אל-פני האדן יקוק', "Three times a year, every male among you must appear before the Presence of the Master, Adonoy.");
insert into verses values (314, 2, 23, 18, 'לא-תזבח על-חמץ דם-זבחי ולא-ילין חלב-חגי עד-בקר', "You must not sacrifice—while chometz is present—My [Pesach] sacrifice. You must not allow to remain overnight—the fat of My festival-offering—until morning.");
insert into verses values (116, 2, 23, 19, 'ראשית בכורי אדמתך תביא בית יקוק אלקיך לא-תבשל גדי בחלב אמו', "The beginning of the first fruits of your land must be brought to the House of Adonoy, your God. You must not cook a young animal in the milk of its mother.");
insert into verses values (513, 2, 23, 24, 'לא-תשתחוה לאלכיכם ולא תעבדם ולא תעשה כמעשיהם כי הרס תהרסם ושבר תשבר מצבתיהם', "You must not prostrate yourself to their gods, nor serve [sacrifice to] them. You must not adopt their customs. You must totally destroy [their idols] and completely break down their monuments.");
insert into verses values (5,   2, 23, 25, 'ועבדתם את יקוק אלקיכם וברך את-לחמך ואת-מימיך והסרתי מחלה מקרבך', "You must serve Adonoy, your God, and He will bless your bread and your water. I will eliminate sickness from your midst.");
insert into verses values (267, 2, 23, 33, 'לא ישבו בארצך פן-יחטיאו אתך לי כי תעבד את-אלהיהם כי-יהיה לך למוקש', "Do not allow them to live in your land, since they may cause you to sin to Me; that you [might] worship their gods, and this would be a [fatal] trap to you.");
insert into verses values (488, 2, 25, 10, 'ועשו ארון אצי שטים אמתים וחצי ארכו ואמה וחצי רחבו ואמה וחצי קמתו', "They shall make an ark of acacia wood, two and one-half amohs long, one and one-half amohs wide, and one and one-half amohs high.");
insert into verses values (294, 2, 25, 15, 'בטבעת הארן יהיו הבדים לא יסרו ממנו', "In the rings of the ark, must the poles remain. They must not be removed from them.");
insert into verses values (24,  2, 25, 30, 'ונתת על-השלחן לחם פנים לפני תמיד', "You shall place the showbread upon the table, before Me at all times.");
insert into verses values (22,  2, 27, 21, 'באהל מועד מחוץ לפרכת אשר על-העדת יערך אתו אהרן ובניו מערב עד-בקר לפני יקוק חקת עולם לדרתם מאת בני ישראל', "In the Tent of Meeting, outside the paroches, which is before the Testimony, Aharon and his sons shall arrange it [for the lamp to burn] from evening to morning before Adonoy. This is an everlasting statute for their generations of the B’nei Yisrael.");
insert into verses values (30,  2, 28, 2,  'ועשית בגדי-קדש לאהרן אחיך לכבוד ולתפארת', "Make sacred garments for your brother, Aharon, for honor and splendor.");
insert into verses values (295, 2, 28, 28, 'וירכסו את-החשן מטבעתו אל-טבעת האפד בפתיל תכלת להיות על-חשב האפוד ולא-יזח החשן מעל האפוד', "They shall bind the [lower] rings of the breastplate to the [lower] rings of the eiphod with a cord of greenish-blue wool, so that it [the breastplate] remains against the eiphod’s belt, so that the breastplate does not move from the eiphod.");
insert into verses values (296, 2, 28, 32, 'והיה פי-ראשו בתוכו שפה יהיה לפיו סביב מעשה ארג כפי תחרא יהיה-לו יקרע', "Its opening at the top shall be turned inward. Its opening shall have a border all around, [this border shall be] of woven work, like there is around the opening of a coat of armor, so that it will not be torn.");
insert into verses values (85,  2, 29, 33, 'ואכלו אתם אשר כפר בהם למלא את-ידם לקדש אתם וזר לא-יאכל כי-קדש הם', "They are to eat those things through which atonement was achieved, to install them and [thereby] to sanctify them. A stranger [a non-kohein] may not eat thereof for they are sacred.");
insert into verses values (326, 2, 29, 34, 'ואם-יותר מבשר המלאים ומן-הלחם עד-הבקר ושרפת את-הנותר באש לא יאכל כי-קדש הוא', "If any meat of the installation-offering is left over or if any of the bread [is left over] until morning, you must burn the leftovers in fire. It must not be eaten for it is sacred.");
insert into verses values (25,  2, 30, 7,  'והקטיר עליו אהרן קטרת סמים בבקר בבקר בהיטיבו את-הנרת יקטירנה', "Aharon shall burn upon it incense of spices. Every morning when he cleans the lamps, he shall burn it.");
insert into verses values (291, 2, 30, 9,  'לא-תעלו עליו קטרת זרה ועלה ומנחה ונסך לא תסכו עליו', "Do not bring upon it strange [unauthorized] incense; neither burnt-offerings nor meal-offerings. You shall not pour a libation-offering upon it.");
insert into verses values (159, 2, 30, 13, 'זה יתנו כל העבר על-הפקדים מחתית השקל בשקל הקדש עשרים גרה השקל מחתית השקל תרומה ליקוק', "Everyone passing by to be counted must give this—half a shekel based on the shekel of the Holy [Sanctuary,] where a shekel is twenty geirah. Half of such a shekel must be given as a terumah-offering to Adonoy.");
insert into verses values (21,  2, 30, 19, 'ורחצו אהרן ובניו ממנו את-ידיהם ואת-רגליהם', "Aharon and his sons will wash from it their hands and their feet.");
insert into verses values (32,  2, 30, 31, 'ואל-בני ישארל תדבר לאמר שמן משחת-קדש יהיה זה לי לדרתיכם', "Speak to the B’nei Yisrael, saying: “This shall be sacred anointing oil to Me for all your generations.");
insert into verses values (292, 2, 30, 32, 'על-בשר אדם לא ייסך ובמתכנתו לא תעשו כמהו קדש הוא קדש יהיה לכם', "It must not be poured on the flesh of any man, and its formula must not be duplicated. It is sacred. It must be sacred to you.");
insert into verses values (293, 2, 30, 37, 'והקטרת אשר תעשה במתכנתה לא תעשו לכם קדש תהיה לך ליקוק', "The incense you are making, its formula must not be duplicated for you[r use]. It shall be sacred to you for Adonoy.");
insert into verses values (77,  2, 34, 20, 'ופטר חמור תפדה בשה ואם-לא תפדה וערפתו כל בכור בניך תפדה ולא-יראו פני ריקם', "The donkey that opens the womb shall be redeemed with a sheep, and if it is not redeemed, you must break its neck. [You must] redeem all first-born of your sons, and you must not appear before Me empty-handed.");
insert into verses values (126, 2, 34, 21, 'ששת ימים תעבד יביום השביעי תשבת בחריש ובקציר תשבת', "Six days you shall work but on the Seventh day you must cease; you must cease plowing and harvesting.");
insert into verses values (313, 2, 34, 25, 'לא-תשחט על-חמץ דם-זבחי ולא-ילין לבקר זבח חג הפסח', "Do not slaughter [the Pesach-offering to offer] the blood of My sacrifice while there is chometz; and do not allow to remain overnight until morning any of the offering of the Pesach Festival.");
insert into verses values (362, 2, 34, 26, 'ראשית בכורי אדמתך תביא בית יקוק אלקיך לא-תבשל גדי בחלב אמו', "The first fruits of your land, you shall bring to the House of Adonoy, your God. You must not cook a kid in its mother’s milk.");
insert into verses values (17,  2, 25, 8,  'ועשו לי מקדש ושכנתי בתוכם', "They shall make a Sanctuary for Me, and I will [then] dwell in their midst.");
insert into verses values (446, 2, 35, 3,  'לא-תבערו אש בכל משבתיכם ביום השבת', "You must not kindle a fire in all your dwelling places on the day of Shabbos.”");
insert into verses values (480, 3, 1,  2,  'דבר אל-בני ישראל ואמרת אלהם אדם כי-יקריב מכם קרבן ליקוק מן-הבהמה מן-הבקר ומן-הצאן תקריבו את-קרבנכם', "Speak to Bnei Yisroel and say to them; [if] a man among you will bring an offering to Adonoy; of animals, cattle or sheep you should bring your offering.");
insert into verses values (59,  3, 1,  3,  'אם-עלה קרבנו מן-הבקר זכר תמים יקריבנו אל-פתח אהל מועד יקריב אתו לרצנו לפמי יקוק', "If his offering is a burnt-offering of cattle he shall bring an unblemished male; to the entrance of the Tent of Meeting he shall bring it, that it be favorably accepted from him before Adonoy.");
insert into verses values (63,  3, 2,  1,  'ונפש כי-תקריב קרבן מנחה ליקוק סלת יהיה קרבנו ויצק עליה שמן ונתן עליה לבנה', "When a person brings a meal-offering to Adonoy, his offering shall be of fine flour; he shall pour oil upon it and place frankincense upon it.");
insert into verses values (304, 3, 2,  11, 'כל-המנחה אשר תקריבו ליקוק לא תעשה חמץ כי כל-שאר וכל-דבש לא-תקטירו ממנו אשה ליקוק', "All meal-offerings that you bring to Adonoy are not to be made leavened, because all sourdough and all honey you shall not burn from them a fire-offering to Adonoy.");
insert into verses values (58,  3, 2,  13, 'וכל-קרבן מנחתך במלח תמלח ולא תשבית מלח ברית אלקיך מעל מנחתך על כל-קרבנך תקריב מלח', "You shall salt all your meal-offerings with salt and you shall not omit salt, the covenant of your God from [being placed] upon your meal-offerings. You shall bring salt on every one of your offerings.");
insert into verses values (64,  3, 4,  13, 'ואם כל-עדת ישראל ישגו ונעלם דבר מעיני הקהל ועשו אחת מכל-מצות יקוק אשר לא-תעשינה ואשמו', "If the entire congregation of Yisroel erred and the matter [that error] was concealed from the eyes of the community, and they did [transgress] one of the commandments of Adonoy that should not be done, they incur guilt.");
insert into verses values (65,  3, 5,  1,  'ונפש כי-תחטא ושמעה קול אלה והוא עד או ראה או ידע אם-לוא יגיד ונשא עונו', "If a person sins, and heard an oath [demanding his testimony] [in a case where] he is a witness; [for] he either saw or knew [something relevant], if he does not testify, he will bear [the burden of] his iniquity.");
insert into verses values (311, 3, 5,  8,  'והביא אתם אל-הכהן והקריב את-אשר לחטאת ראשונה ומלק את-ראשו ממול ערפו ולא יבדיל', "He shall bring them to the kohein and he shall bring that which is for a sin-offering first. He [the kohein] shall shear its head [with his fingernail] opposite of its neck, but not separate [the head] completely.");
insert into verses values (68,  3, 5,  11, 'ואם-לא תשיג ידו לשתי תרים או לשני בני-יונה והביא את-קרבנו אשר חטא עשירת האפה סלת לחטאת לא-ישים עליה שמן ולא-יתן עליה לבנה כי חטאת היא', "If his means are not sufficient for the acquisition of two turtledoves or two young pigeons, he shall bring as his offering,—[he] who has sinned,—one tenth of an epha of fine flour as a sin-offering. He shall not put oil upon it, nor shall he place frankincense upon it, for it is a sin-offering.");
insert into verses values (67,  3, 5,  15, 'נפש כי-תמעל מעל וחטאה בשגגה מקדשי יקוק והביא את-אשמו ליקוק איל תמים מן-הצאן בערכך כסף-שקלים בשקל-הקדש לאשם', "If a person wrongfully deviates and unwittingly sins [by using the] sacred property of Adonoy, he shall bring his guilt-offering to Adonoy, an unblemished ram, from the sheep, based on the valuation of holy silver shekalim, for a guilt-offering.");
insert into verses values (66,  3, 5,  17, 'ואם-נפש כי תחטא ועשתה אחת מכל-מצות יקוק אשר לא תעשינה ולא-ידע ואשם ונשא עונו', "If a person sins and did [transgress] one of the commandments of Adonoy that should not be done, [however,] he is uncertain [that he sinned], and he [nevertheless] incurs guilt and will bear [the burden of] his iniquity.");
insert into verses values (181, 3, 5,  23, 'והיה כי-יחטא ואשם והשיב את-הגזלה אשר גזל או את-העשק אשר עשק או את-הפקדון אשר הפקד אתו או את-האבדה אשר מצא', "It shall be that when he [realizes his] sin and incurs guilt, he shall return the stolen property that he stole, or, the wages that he withheld or, whatever keepsake that was placed in his trust or, the lost item which he found.");
insert into verses values (27,  3, 6,  3,  'ולבש הכהן מדו בד ומכנסי-בד ילבש על-בשרו והרים את-הדשן אשר תאכל האש את-העלה על-המזבח ושמו אצל המזבח', "The kohein shall dress in his linen garment and he shall wear linen pants on his [body] skin; and he shall separate the ashes when the fire consumes the burnt-offering on the altar and place it next to the altar.");
insert into verses values (481, 3, 6,  5,  'והאש על-המזבח תוקד-בו לא תכבה ובער עליה הכהן עצים בבקר בבקר וערך עליה העלה והקטיר עליה חלבי השלמים', "The fire on the altar shall be kindled with it it shall not go out. The kohein shall burn logs upon it each and every morning and arrange the burnt-offering on it and burn upon it the fats of the peace-offering.");
insert into verses values (26,  3, 6,  6,  'אש תמיד תוקד על-המזבח לא תכבה', "A continual fire shall be lit on the altar; it shall not go out.");
insert into verses values (84,  3, 6,  9,  'והנותרת ממנה יאכלו אהרן וביו מצות תאכל במקום קדש בחצר אהל-מועד יאכלוה', "Whatever remains from it shall be eaten by Aharon and his sons. It must be eaten as matzoh, in a sacred place. In the courtyard of the Tent of Meeting they shall eat it.");
insert into verses values (320, 3, 6,  10, 'לא תאפה חמץ חלקם נתתי אתה מאשי קדש קדשים הוא כחטאת וכאשם', "It should not be baked [as] leavened. I have given it to them as their portion from my fire-offerings. It is holy of holies [most holy], as [are] the sin-offering and as the guilt-offering.");
insert into verses values (37,  3, 6,  13, 'זה קרבן אהרן ובניו אשר-יקריבו ליקוק ביום המשח אתו עשירת האפה סלת מנחה תמיד מחציתה בבקר ומחציתה בערב', "This is the offering of Aharon and his sons that they shall offer to Adonoy on the day that he is anointed; one tenth of an ephah of fine flour as a meal-offering, always [daily]. Half of it [he shall offer] in the morning and half of it [he shall offer] in the evening.");
insert into verses values (330, 3, 6,  16, 'וכל-מנחת כהן כליל תהיה לא תאכל', "Every meal-offering of a kohein shall be completely burnt and may not be eaten.");
insert into verses values (60,  3, 6,  18, 'דבר אל-אהרן ואל-בניו לאמר זאת תורת החטאת במקום אשר תשחט העלה תשחט החטאת לפני יקוק קדש קדשים הוא', "Speak to Aharon and his sons, saying: this is the law of the sin-offering. In the place where the burnt-offering is slaughtered, the sin-offering shall be slaughtered, before Adonoy; it is holy of holies [most holy].");
insert into verses values (331, 3, 6,  23, 'וכל-חטאת אשר יובא מדמה אל-אהל מועד לכפר בקדש לא תאכל באש תשרף', "Every sin-offering whose blood is to be brought inside the Tent of Meeting to bring about atonement inside the Sanctuary may not be eaten; it must be burned in fire.");
insert into verses values (61,  3, 7,  1,  'וזאת תורת האשם קדש קדשים הוא', "This is the law of the guilt-offering; it is holy of holies [=most holy].");
insert into verses values (62,  3, 7,  11, 'וזאת תורת זבח השלמים אשר יקריב ליקוק', "This is the law of the peace-offering that one will bring to Adonoy.");
insert into verses values (87,  3, 7,  17, 'והנותר מבשר הזבח ביום השלישי באש ישרף', "Whatever flesh is left over from the offering shall be burned in fire, on the third day.");
insert into verses values (495, 3, 7,  18, 'ואם האכל יאכל מבשר-זבח שלמיו ביום השלישי לא ירצה המקריב אתו לא יחשב לו פגול יהיה והנפש האכלת ממנו עונה תשא', "If it will be eaten, of the flesh of the peace-offering on the third day, it will not be favorably accepted. [As to] whoever brings it, it will not be credited to him; it is an abomination and anyone who eats of it shall bear [the burden of] its iniquity.");
insert into verses values (86,  3, 7,  19, 'והבשר אשר-יגע בכל-טמא לא יאכל באש ישרף והבשר כל-טהור יאכל בשר', "The flesh [of offerings] that will touch any unclean [thing] may not be eaten; it shall be burned in fire. [As to] the [pure] flesh, every [ritually] pure person may eat [the] meat.");
insert into verses values (361, 3, 7,  23, 'דבר אל-בני ישראל לאמר כל-חלב שור וכשב ועז לא תאכלו', "Speak to Bnei Yisroel, saying: any fat of oxen, sheep or goats you must not eat.");
insert into verses values (360, 3, 7,  26, 'וכל-דם לא תאכלו בכל מושבתיכם לעוף ולבהמה', "You shall not eat any blood, in all your dwelling places, be it of fowl or of beast.");
insert into verses values (342, 3, 10, 6,  'ויאמר משה אל-אהרן ולאלעזר ולאיתמר בניו ראשיכם אל-תפרעו ובגדיכם לא-תפרמו ולא תמתו ועל כל-העדה יקצף ואחיכם כל-בית ישראל יבכו את-השרפה אשר שרף יקוק', "Moshe said to Aharon and to Elozor and Isomor, his sons: Do not let your hair grow long and do not tear your garments so that you will not die and bring [Divine] anger on the entire congregation; your brothers, the entire House of Yisroel shall weep over the conflagration that Adonoy set ablaze.");
insert into verses values (343, 3, 10, 7,  'ומפתח אהל מועד לא תצאו פן-תמתו כי-שמן משחת יקוק עליכם ויעשו כדבר משה', "From the entrance of the Tent of Meeting you shall not go forth lest you die; for the anointing oil of Adonoy is on you and they did as Moshe had said.");
insert into verses values (283, 3, 10, 9,  'יין ושכר אל-תשת אתה ובניך אתך בבאכם אל-אהל מועד ולא תמתו חקת עולם לדרתיכם', "Do not drink wine or any other intoxicant, you and your sons with you, when you enter the Tent of Meeting and you will not die; this is an everlasting statute throughout your generations.");
insert into verses values (490, 3, 10, 11, 'ולהורת את-בני ישראל את כל-החקים אשר דבר יקוק אליהם ביד-משה', "[And that you may] teach Bnei Yisroel all the statutes that Adonoy spoke to them, through Moshe.");
insert into verses values (515, 3, 10, 19, 'וידבר אהרן אל-משה הן היום הקריבו את-חטאתם ואת-עלתם לפני יקוק ותקראנה אתי כאלה ואכלתי חטאת היום הייטב בעיני יקוק', "Aharon spoke [in reply] to Moshe, Behold, today they brought their sin-offerings and their burnt-offerings before Adonoy and then such [tragedy] befell me. If I had eaten the sin-offering today, would that have been good in the eyes of Adonoy?");
insert into verses values (138, 3, 11, 2,  'דברו אל-בני ישראל לאמר זאת החיה אשר תאכלו מכל-הבהמה אשר על-הארץ', "Speak to Bnei Yisroel, saying: these are the living things that you may eat from among all the animals that are on the earth.");
insert into verses values (348, 3, 11, 4,  'אך את-זה תאכלו ממעלי הגרה וממפריסי הפרסה את-הגמל כי-מעלה גרה הוא ופרסה איננו מפריס טמא הוא לכם', "However, these in particular you may not eat of those that chew their cud and that have cloven hooves: the camel, for he chews his cud but does not have a split hoof it is unclean to you.");
insert into verses values (141, 3, 11, 9,  'את-זה תאכלו מכל אשר במים כל אשר-לו סנפיר וקשקשת במים בימים ובנחלים אתם תאכלו', "These you may eat, of all things that are in water; all that have fins and scales in the waters, in oceans and in rivers; you may eat them.");
insert into verses values (349, 3, 11, 11, 'ושקץ יהיו לכם מבשרם לא תאכלו ואת-מבלתם תשקצו', "They shall be repulsive to you, you shall not eat of their flesh and their carcasses shall be repulsive by you.");
insert into verses values (350, 3, 11, 13, 'ואת-אלה משקצו מן-העוף לא יאכלו שקץ הם את-הנשר ואת-הפרס ואת העזניה', "These you shall consider repulsive from among the fowl; they may not be eaten; they are repulsive: the eagle, the white-tailed eagle and the bearded vulture.");
insert into verses values (140, 3, 11, 21, 'אך את-זה לאכלו מכל שרץ העוף ההלך על-ארבע אשר-לא כרעים ממעל לרגליו לנתר בהן על-הארץ', "However, these you may eat of all the flying insects that walk on four [legs], those which have knees extending above their legs so that it hops on the ground with them.");
insert into verses values (93,  3, 11, 29, 'וזה לכם הטמא בשרץ השרץ על-הארץ החלד והעכבר והצב למינהו', "These [also] are unclean to you among all creeping creatures that crawl on the ground; the weasel, the mouse and the toad, to its kind.");
insert into verses values (94,  3, 11, 34, 'מכל-האכל אשר יאכל אשר יבוא עליו מים יטמא וכל-משקה אשר ישתה בכל-כלי יטמא', "Of all foods that are eaten upon which water comes, shall be unclean. Every beverage that is drunk, in any vessel, shall be impure.");
insert into verses values (92,  3, 11, 39, 'יכי-ימות מן-הבהמה אשר-היא לכם לאכלה הנגע בנבלתה יטמא עד-הערב', "If any animal dies [of those] that are permitted to eat whoever touches its carcass shall be unclean until the evening.");
insert into verses values (352, 3, 11, 41, 'וכל-השרץ השרץ על-הארץ שקץ הוא לא יאכל', "Every creeping creature which crawls on the ground is repulsive, it may not be eaten.");
insert into verses values (354, 3, 11, 42, 'כל הולך על-גחון וכל הולך על-ארבע עד כל-מרבה רגלים לכל-השרץ השרץ על-הארץ לא תאכלום כי-שקץ הם', "Whatever crawls on its belly and whatever goes on four [legs] or whatever has many feet; regarding all creeping creatures that crawl on the ground; you may not eat them, for they are repulsive.");
insert into verses values (355, 3, 11, 43, 'אל-תשקצו את-נפשתיכם בכל-השרץ השרץ ולא תטמאו בהם ונטמתם בם', "Do not make yourselves repulsive by [eating] any creeping creature that crawls, and do not make yourselves unclean with them, for you will become unclean because of them.");
insert into verses values (353, 3, 11, 44, 'כי אני יקוק אלקיכם והתקדשתם והייתם קדשים כי קדוש אני ולא תטמאו את-נפשתיכם בכל השרץ הרמש על-הארץ', "For I am Adonoy, your God; you shall sanctify yourselves and you shall become holy, for I am Holy and you shall not make yourselves unclean with any creeping creature that crawls on the ground.");
insert into verses values (96,  3, 12, 2,  'דבר אל-בני ישראל לאמר אשה כי תזריע וילדה זכר וטמאה שבעת ימים כימי נדת דותה תטמא', "Speak to Bnei Yisroel, saying, when a woman conceives and gives birth to a male child she will be ritually unclean for seven days; as the days of her menstrual flow, she will be unclean.");
insert into verses values (325, 3, 12, 4,  'ושלשים יום ושלשת ימים תשב בדמי טהרה בכל-קדש לא-תגע ואל-המקדש לא תבא עד-מלאת ימי טהרה', "For thirty-three days she will remain [in the status] that the blood [she sees] is ritually pure. She shall not touch anything holy and she shall not come into the Sanctuary, until the days of her purity are completed.");
insert into verses values (72,  3, 12, 6,  'ובמלאת ימי טהרה לבן או לבת תביא כבש בן-שנתו לעלה ובן-יונה או-תר לחטאת אל-פתח אהל-מועד אל-הכהן', "When the days of her purification are completed, be it for a son or for a daughter, she shall bring a lamb, in its first year, as a burnt-offering and a young pigeon or a turtledove as a sin-offering, to the entrance of the Tent of Meeting, to the kohein.");
insert into verses values (97,  3, 13, 3,  'וראה הכהן את-הנגע בעור-הבשר ושער בנגע הפך לבן ומראה הנגע עמק מעור בשרו נגע צרעת הוא וראהו הכהן וטמא אתו', "The kohein shall see the plague in the skin of the flesh and [if] the hair in the skin-eruption has turned white and the appearance of the plague is deeper than the skin of his body, it is the skin-eruption of tzoraas. The kohein shall see it and declare him [to be] unclean.");
insert into verses values (438, 3, 13, 33, 'והתגלח ואת-הנתק לא יגלח והסגיר הכהן את-הנתק שבעת ימים שנית', "He shall be shaved but he shall not shave the nesek, and the kohein shall confine the nesek for a second seven day period.");
insert into verses values (107, 3, 13, 45, 'והצרוע אשר-בו הנגע בגדיו יהיו פרמים וראשו יהיה פרוע ועל-שפם יעטה וטמא טמא יקרא', "The tzorua—[the person] who has the skin-eruption—his garments shall have a tear in them, his hair shall be grown long, and he must cover his head down to his upper lip, and Unclean, unclean, shall he call out.");
insert into verses values (98,  3, 13, 51, 'וראה את-נגע ביום השביעי כי-פשה הנגע בבגד או-בשתי או-בערב או בעור לכם אשר-יעשה העור למלאכה צרעת ממארת הנגע טמא הוא', "He shall see the eruption on the seventh day. If the eruption has spread in the garment or in the warp thread, or in the woof thread, or in the leather hide, or in the work made of leather, it is a prickly tzoraas, it is unclean.");
insert into verses values (262, 3, 14, 1,  'בנים אתם ליקוק אלקיכם לא תתגדדו ולא-תשימו קרחה בין עיניכם למת', "And Adonoy spoke to Moshe, saying:");
insert into verses values (105, 3, 14, 2,  'וזאת תהיה תורת המצרע ביום טהרתו והובא אל-הכהן', "This shall be the law regarding the person with tzoraas on the day of his purification, he shall be brought to the kohein.");
insert into verses values (106, 3, 14, 9,  'והיה ביום השביעי יגלח את-כל-שערו את-ראשו ואת-זקנו ואת גבת עיניו ואת-כל-שערו יגלח וכבס את-בגדיו ורחץ את-בשרו במים וטהר', "It shall be, that on the seventh day, he shall shave off all of his hair, [that of] his head, his beard and his eyebrows; he shall shave off all of his hair. He shall wash his garments, and bathe his body in water and become purified.");
insert into verses values (73,  3, 14, 10, 'וביום השמיני יקח שני-כבשים תמימים וכבשה אחת בת-שנתה תמימה ושלשה עשרנים סלת מנחה בלולה בשמן ולג אחד שמן', "On the eighth day, he shall take two unblemished lambs and one ewe in her first year, unblemished, and three tenths fine flour, for a meal-offering, mixed with oil and one log of oil.");
insert into verses values (99,  3, 14, 44, 'ובא הכהן וראה והנה פשה הנגע בבית צרעת ממארת הוא בבית טמא הוא', "the kohein shall come and see; and behold! the eruption has spread in the house; prickly tzoraas is in the house, it is impure.");
insert into verses values (100, 3, 15, 2,  'דברו אל-בני ישראל ואמרתם אלהם איש איש כי יהיה זה מבשרו זוהו טמא הוא', "Speak to Bnei Yisroel and say to them; should [discharge] flow from the body of any man, [because of] his discharge, he is impure.");
insert into verses values (70,  3, 15, 13, 'וכי-יטהר הזב מזובו וספר לו שבעת ימים לטהרתו וכבס בגדיו ורחץ בשרו במים חיים וטהר', "When a zov will become ritually clean, from his discharge-impurity, he shall count for himself seven days for his purification and wash his garments, and bathe in running water, and he will be purified.");
insert into verses values (101, 3, 15, 16, 'ואיש כי-תצא ממנו שכבת-זרע ורחץ במים את-כל-בשרו וטמא עד-הערב', "A man, if semen issues from him, he shall bathe his entire body in water and is impure until the evening.");
insert into verses values (102, 3, 15, 19, 'ואשה כי-תהיה זבה דם יהיה זבה בבשרה שבעת ימים תהיה בנדתה וכל-הנגע בה יטמא עד-הערב', "When a woman has a discharge [and] the discharge will be blood in her body, for seven days she will be in [the state of] her menstrual impurity. Anyone who touches her will be impure until the evening.");
insert into verses values (95,  3, 15, 25, 'ואשה כי-יזוב זוב דמה ימים רבים בלא עת-נדתה או כי-תזוב על-נדתה כל-ימי זוב טמאתה כימי נדתה תהיה טמאה הוא', "When a woman discharges her blood-flow, many days outside her menstrual cycle, or if it flows after her menstrual cycle, all the days of her discharge-impurity shall be as her days of menstrual impurity; she is impure.");
insert into verses values (71,  3, 15, 28, 'ואם-טהרה מזובה וספרה לה שבעת ימים ואחר תטהר', "When she has become pure from her discharge-impurity, she shall count seven days for herself and afterwards she becomes purified.");
insert into verses values (279, 3, 16, 2,  'ויאמר יקוק אל-משה דבר אל-אהרן אחיך ואל-יבא בכל-עת אל-הקדש מבית לפרכת אל-פני הכפרת אשר על-הארן ולא ימות כי בענן אראה על-הכפרת', "Adonoy spoke to Moshe: Speak to your brother Aharon that he not come at all times into the Holy [Sanctuary] that is inside of the Curtain, before the Ark-cover that is on the Ark so that he not die, for in a cloud I shall appear on the Ark-cover.");
insert into verses values (46,  3, 16, 3,  'בזאת יבא אהרן אל-הקדש בפר בן-בקר לחטאת ואיל לעלה', "With this shall Aharon come into the Holy [Sanctuary]; with a young bullock, for a sin-offering and a ram, for a burnt-offering.");
insert into verses values (152, 3, 16, 29, 'והיתה לכם לחקת עולם בחדש השביעי בעשור לחדש תענו את-נפשתיכם וכל-מלאכה לא תעשו האזרח והגר הגר בתוככם', "It shall be for an everlasting statute for you; in the seventh month, on the tenth of the month, you shall afflict yourselves and not do any work, the native-born and the proselyte residing among you.");
insert into verses values (153, 3, 16, 31, 'שבת שבתון היא לכם ועניתם את-נפשתיכם חקת עולם', "It is a Shabbos of Shabbosos to you and you must afflict yourselves; it is an everlasting statute.");
insert into verses values (298, 3, 17, 3,  'איש איש מבית ישראל אשר ישחט שור או-כשב או-עז במחנה או אשר ישחט מחוץ למחנה', "Each and every person of the House of Israel who will slaughter an ox, lamb, or goat within the encampment or who will slaughter [it] beyond the encampment.");
insert into verses values (136, 3, 17, 13, 'ואיש איש מבני ישראל ומן-הגר הגר בתוכם אשר יצוד ציד חיה או-עוף אשר יאכל ושפך את-דמו וכסהו בעפר', "Each and every person of the House of Israel and from the proselyte residing among them who will ensnare [the catch] any wild animal or fowl that is permitted to be eaten, he shall spill its blood and cover it with earth.");
insert into verses values (465, 3, 18, 6,  'איש איש אל-כל-שאר משרו לא תקרבו לגלות ערוה אני יקוק', "Each and every person—to any of his close kin—You shall not approach to uncover [their] nakedness, I am Adonoy.");
insert into verses values (449, 3, 18, 7,  'ערות אביך וערות אמך לא תגלה אמך הוא לא תגלה ערותה', "The nakedness of your father and the nakedness of your mother you shall not uncover; she is your mother, you shall not uncover her nakedness.");
insert into verses values (450, 3, 18, 8,  'ערות אשת-אביך לא תגלה ערות אביך הוא', "The nakedness of your fathers wife you shall not uncover; it is your fathers nakedness.");
insert into verses values (451, 3, 18, 9,  'ערות אחותך בת-אביך או בת-אמך מולגת בית או מולדת חוץ לא תגלה ערותן', "The nakedness of your sister [whether she is] your fathers daughter or your mothers daughter; whether she is born in the house, or she is born outside, you shall not uncover their nakedness.");
insert into verses values (453, 3, 18, 10, 'ערות בת-בנך או בת-בתך לא תגלה ערותן כי ערותך הנה', "The nakedness of your sons daughter or your daughters daughter; you shall not uncover their nakedness, for their nakedness is your own.");
insert into verses values (452, 3, 18, 11, 'ערות בת-אשת אביך מולדת אביך אחותך הוא לא תגלה ערותה', "The nakedness of the daughter of your fathers wife who is born to your father; she is your sister, you shall not uncover her nakedness.");
insert into verses values (455, 3, 18, 12, 'ערות אחות-אביך לא תגלה אשר אביך הוא', "The nakedness of your fathers sister you shall not uncover; she is your fathers kin.");
insert into verses values (456, 3, 18, 13, 'ערות אחות-אמך לא תגלה כי-שאר אמך הוא', "The nakedness of your mothers sister you shall not uncover, for she is your mothers kin.");
insert into verses values (457, 3, 18, 14, 'ערות אחי-אביך לא תגלה אל-אשתו לא תקרב דדתך הוא', "The nakedness of your fathers brother you shall not uncover. You shall not approach his wife, she is your aunt.");
insert into verses values (458, 3, 18, 15, 'ערות כלתך לא תגלה אשת בנך הוא לא תגלה ערותה', "The nakedness of your daughter-in-law you shall not uncover; she is your sons wife, you shall not uncover her nakedness.");
insert into verses values (459, 3, 18, 16, 'ערות אשת-אחיך לא תגלה ערות אחיך הוא', "The nakedness of your brothers wife you shall not uncover; it is your brothers nakedness.");
insert into verses values (454, 3, 18, 17, 'ערות אשה ובתה לא תגלה את-בת-בנה ואת-בת-בתה לא תקח לגלות ערותה שארה הנה זמה הוא', "The nakedness of a woman and her daughter you shall not uncover. The daughter of her son and the daughter of her daughter you shall not take to uncover her nakedness, they are kin; it is lewdness.");
insert into verses values (460, 3, 18, 18, 'ואשה אל-אחתה לא תקח לצרר לגלות ערותה עליה בחייה', "You shall not take a woman and her sister to vex [her] to uncover her nakedness with the other, in her lifetime.");
insert into verses values (461, 3, 18, 19, 'ואל-אשה בנדת טמאתה לא תקרב לגלות ערותה', "To a woman while in her menstrual impurity, you shall not approach to uncover her nakedness.");
insert into verses values (462, 3, 18, 20, 'ואל-אשת אמיתך לא-תתן שכבתך לזרע לטמאה-בה', "With your friends wife you shall not lie carnally, to defile yourself through her.");
insert into verses values (239, 3, 18, 21, 'ומזרעך לא-תתן להעביר למלך ולא תחלל את-שם אלקיך אני יקוק', "You shall not hand over any of your children to be passed through [the worship of] Molech and you shall not defile the Name of your God, I am Adonoy.");
insert into verses values (464, 3, 18, 22, 'ואת-זכר לא תשכב משכבי אשה תועבה הוא', "You shall not lie with a male [conjugally] as one lies with a woman; it is an abomination.");
insert into verses values (463, 3, 18, 23, 'ובכל-בהמה לא-תתן שכבתך לטמאה-בה ואשה לא-תעמד לפני בהמה לרבעה תבל הוא', "Together with any animal you shall not lie [conjugally] to defile yourself with it. A woman shall not stand before an animal to copulate with it; it is defilement.");
insert into verses values (198, 3, 19, 3,  'איש אמו ואביו תיראו ואת-שבתתי תשמרו אני יקוק אלקיכם', "A man—you shall fear his mother and father and keep My Sabbaths, I am Adonoy, your God.");
insert into verses values (236, 3, 19, 4,  'אל-תפנו אל-האלילים ואלהי מסכה לא תעשו לכם אני יקוק אלקיכם', "Do not turn to the idols and do not make molten gods for yourselves, I am Adonoy, your God.");
insert into verses values (379, 3, 19, 9,  'ובקצרכם את-קציר ארצכם לא תכלה פאת שדך לקצר ולקט קצירך לא תלקט', "When you will reap the harvest of your land, you shall not completely reap a corner of your field, and the gleanings of your harvest you shall not gather.");
insert into verses values (114, 3, 19, 10, 'וכרמך לא תעולל ופרט כרמך לא תלקט לעני ולגר תעזב אתם אני יקוק אלקיכם', "You shall not glean your vineyard, and the fallen grapes of your vineyard, you shall not gather. For the poor and the proselyte you shall leave them, I am Adonoy, your God.");
insert into verses values (401, 3, 19, 11, 'לא תגנבו ולא-תכחשו ולא-תשקרו איש בעמיתו', "You shall not steal. You shall not deny [a rightful claim] and each one of you shall not lie to each other.");
insert into verses values (275, 3, 19, 12, 'ולא-תשבעו בשמי לשקר וחללת את-שם אלקיך אני יקוק', "You shall not swear falsely by My Name profaning the Name of your God, I am Adonoy.");
insert into verses values (395, 3, 19, 13, 'לא-תעשק את-רעך ולא תגזל לא-תלין פעלת שכיר אתך עד-בקר', "You shall not withhold the wages of your fellow [Jew] and you shall not rob. It shall not remain overnight, the wages of a day-laborer, keeping it in your possession until morning.");
insert into verses values (435, 3, 19, 14, 'לא-תקלל חרש ולפני עור לא תתן מכשל ויראת מאלקיך אני יקוק', "You shall not curse a deaf person and before a blind person you shall not place a stumbling-block; you shall fear your God, I am Adonoy.");
insert into verses values (165, 3, 19, 15, 'לא-תעשו עול במשפט לא-תשא פני-דל ולא תהדר פני גדול בצדק תשפט עמיתך', "You shall not commit injustice in judgement, you shall not favor a poor man and you shall not show honor to a powerful man. With righteousness shall you judge your fellow [Jew].");
insert into verses values (434, 3, 19, 16, 'לא-תלך רכיל בעמיך לא תעמד על-דם רעך אני יקוק', "Do not be a tale-bearer among your people. You shall not stand idly by [when] the blood (life) of your fellow [is in danger]. I am Adonoy.");
insert into verses values (192, 3, 19, 17, 'לא-תשנא את-אחיך בלבבך הוכח תוכיח את-עמיתך ולא-תשא עליו חטא', "You shall not hate your brother in your heart; You shall certainly rebuke your friend; [but] you shall not bear a sin on his account.");
insert into verses values (193, 3, 19, 18, 'לא-חקם ולא תטר את-בני עמך ואהבת לרעך כמוך אני יקוק', "You shall not take revenge or bear a grudge against the children of your people. You shall love your fellow [Jew] as yourself, I am Adonoy.");
insert into verses values (380, 3, 19, 19, 'את-חקתי תשמרו בהמתך לא-תרביע כלאים שדך לא-תזרע כלאים ובגד כלאים שעטנז לא יעלה עליך', "Keep My statutes; Do not cross-breed your animals with different species. Do not sow your field with a mixture of different seeds. A garment that contains shatnez [a forbidden mixture of linen and wool] you shall not wear.");
insert into verses values (365, 3, 19, 23, 'וכי-תבאו אל-הארץ ונטעתם כל-עץ מאכל וערלתם ערלתו את-פריו שלש שנים יהיה לכם ערלים לא יאכל', "When you will enter the land and you will plant any food-bearing tree, you shall regard its fruit as closed off. [For] three years it shall be closed off to you, it shall not be eaten.");
insert into verses values (113, 3, 19, 24, 'ובשנה הרביעת יהיה כל-פריו קדש הלולים ליקוק', "On the fourth year, all of its fruit shall be holy [upon which to say] praise[s to] Adonoy.");
insert into verses values (368, 3, 19, 26, 'לא תאכלו על-הדם לא תנחשו ולא תעוננו', "You shall not eat [sacred offerings] [before] the blood [is sprinkled on the altar]. You shall not act on the basis of omens nor act on the basis of auspicious times.");
insert into verses values (260, 3, 19, 27, 'לא תקפו פאת ראשכם ולא תשחית את פאת זקנך', "Do not round the corners of [the hair on] your heads and you shall not destroy [by shaving] the edges of your beard.");
insert into verses values (258, 3, 19, 28, 'ושרט לנפש לא תתמו בבשרכם וכתבת קעקע לא תתנו בכם אני יקוק', "Gashes for the dead do not make in your skin. Do not put tattoo marks on yourselves, I am Adonoy.");
insert into verses values (507, 3, 19, 29, 'אל-תחלל את-בתך להזנותה ולא-תזנה הארץ ומלאה הארץ זמה', "You shall not defile your daughter by causing her to engage in harlotry lest the land be turned astray, and the land be filled with lewdness.");
insert into verses values (18,  3, 19, 30, 'את-שבתתי תשמרו ומקדשי תיראו וני יקוק', "You shall keep My Shabbosos and you shall fear My Sanctuary, I am Adonoy.");
insert into verses values (240, 3, 19, 31, 'אל-תפנו אל-האבת ואל-הידענים אל-תבקשו לטמאה בהם אני יקוק אלקיכם', "You shall not turn to mediums nor seek out oracles; do not seek [them] out, so as to defile yourselves through them. I am Adonoy, your God.");
insert into verses values (196, 3, 19, 32, 'מפני שיבה תקום והדרת פני זקן ויראת מאלקיך אני יקוק', "In the presence of the elderly you shall rise and you shall respect an elder; you shall have fear of your God, I am Adonoy.");
insert into verses values (418, 3, 19, 35, 'לא-תעשו עול במשפט במדה במשקל ובמשורה', "You shall not commit injustice in judgement; in [land] measure, in weight or in volume measure.");
insert into verses values (195, 3, 19, 36, 'מאזני צדק אבני-צדק איפת צדק והין צדק יהיה לכם אני יקוק אלקיכם אשר-הוצאתי אתכם מארץ מצרים', "Just scales and just weights a just dry measure (ephah) and a just liquid measure (hin) you shall have for yourselves, I am Adonoy, your God who has taken you out of the land of Egypt.");
insert into verses values (213, 3, 20, 10, 'ואיש אשר ינאף את-אשת איש אשר ינאף את-אשת רעהו מות-יומת הנאף והנאפת', "A man who commits adultery with another mans wife who commits adultery with the wife of his fellow man—they shall surely be put to death—the adulterer and the adulteress.");
insert into verses values (214, 3, 20, 14, 'ואיש אשר יקח את-אשה ואת-שמה זמה הוא באש ישרפו אתו ואתהן ולא-תהיה זמה בתוככם', "A man who marries a woman and her mother; it is lewdness. They shall be burned in fire, he and [one of] them, and there will no longer be such lewdness among you.");
insert into verses values (253, 3, 20, 23, 'ולא תלכו בחקת הגוי אשר-אני משלח מפניכם כי את-כל-אלה עשו ואקיץ בם', "You shall not follow the customs of the nation which I am expelling before you. For they have done all these above mentioned sins and I became disgusted with them.");
insert into verses values (344, 4, 21, 1,  'ויאמר יקוק אל-משה אמר אל-הכהנים בני אהרן ואמרת אלהם לנפש לא-יטמא בעמיו', "The Canaanite King of Arad heard—who lived in the south—that Yisroel had come by the route of the spies; he attacked Yisroel, and he took from them a captive.");
insert into verses values (34,  3, 21, 3,  'ולאחתו הבתולה הקרובה אליו אשר לא-היתה לאיש לה חטמא', "For his virgin sister, to whom he is closely related, who has not yet been married, for her, he may be defiled.");
insert into verses values (286, 3, 21, 6,  'קדשים יהיו לאלקיהם ולא יחללו שם אלקיהם כי את-אשי יקוק לחם אלקיהם הם מקריבם והיו קדש', "They shall be holy to their God and they shall not profane the Name of their God, for [it is] the fire-offerings of Adonoy, the bread of their God [that] they bring [offer], and they must [therefore] be holy.");
insert into verses values (339, 3, 21, 7,  'אשה זונה וחללה לא יקחו ואשה גרושה מאישה לא יקחו כי-קדש הוא לאלקיו', "A harlot or a profaned woman they shall not marry. A woman divorced from her husband they shall not marry, for he is holy to his God.");
insert into verses values (29,  3, 21, 8,  'וקדשתו כי-את-לחם אלקיך הוא מקריב קדש יהיה-לך כי קדוש אני יקוק מקדשכם', "You shall make him holy for he offers the bread of your God; he shall be holy unto you for I, Adonoy, am holy, Who makes you holy.");
insert into verses values (345, 3, 21, 11, 'ועל כל-מפשת מת לא יבא לאביו ולאמו לא יטמא', "And adjacent to any dead body he shall not enter. For his father and mother he shall not become [ritually] impure.");
insert into verses values (508, 3, 21, 12, 'ומן-המקדש לא יצא ולא יחלל את מקדש אלקיו כי נזר שמן משחת אלקיו עליו אני יקוק', "He shall not depart from the Sanctuary and he shall not profane his Gods Sanctuary, for the crown of his Gods anointing oil is upon him, I am Adonoy.");
insert into verses values (35,  3, 21, 13, 'והוא אשה בבתוליה יקח', "He shall marry [only] a virgin.");
insert into verses values (340, 3, 21, 14, 'אלמנה וגרושה וחללה זנה את-אלה לא יקח כי אם-בתולה מעמיו יקח אשה', "A widow, divorcee, profaned woman or harlot; he may not marry [any] of these. But only a virgin from his own people may he marry.");
insert into verses values (341, 3, 21, 15, 'ולא-יחלל זרעו בעמיו כי אני יקוק מקדשו', "He shall not profane his offspring among his people, for I am Adonoy, who makes him holy.");
insert into verses values (280, 3, 21, 17, 'דבר אל-אהרן לאמר איש מזערך לדרתם אשר יהיה בו מום לא יקרב להקריב לחם אלקיו', "Speak to Aharon saying; Anyone of your offspring, through [all] their generations who will have a blemish, shall not approach to offer the bread of his God.");
insert into verses values (281, 3, 21, 18, 'כי כל-איש אשר-בו מום לא יקרב איש עור או פסח או חרם או שרוע', "For any man who has a blemish shall not approach; a blind man or a lame man, or a man with a sunken nose, or a man who has disproportionate limbs.");
insert into verses values (285, 3, 22, 2,  'דבר אל-אהרן ואל-בניו וינזרו מקדשי בני-ישראל ולא יחללו את-שם קדשי אשר הם מקדשים לי אני יקוק', "Speak to Aharon and to his sons and let them keep away from the holy offerings of Bnei Yisroel and they shall not profane My holy name which they sanctify for Me, I am Adonoy.");
insert into verses values (328, 3, 22, 4,  'איש איש מזרע אהרן והוא צרוע או זב בקדשים לא יאכל עד אשר יטהר והנגע בכל-טמא-נפש או איש אשר-תצא ממנו שכבת-זרע', "Any man of Aharons offspring; who has tzoraas or an impure flow, may not eat of the sacred things until he has purified himself. [The same is true] of anyone who touches anything defiled by the dead, or anyone who has had a seminal emission,");
insert into verses values (327, 3, 22, 10, 'וכל-זר לא-יאכל קדש תושב כהן ושכיר לא-יאכל קדש', "No stranger (non-kohein) may eat holy things. One indentured to a kohein and one hired [by a kohein] may not eat sacred-offerings.");
insert into verses values (329, 3, 22, 12, 'ובת-כהן כי תהיה לאיש זר הוא בתרומת הקדשים לא תאכל', "When a koheins daughter marries a non-kohein, the sacred terumah portions may not be eaten by her.");
insert into verses values (335, 3, 22, 15, 'ולא יחללו את-קדשי בני ישראל את אשר-ירימו ליקוק', "They shall not profane the holy things of Bnei Yisroel that which they designated for Adonoy.");
insert into verses values (299, 3, 22, 20, 'כל אשר-בו חמום לא תקריבו כי-לא לרצון יהיה לכם', "Anything that has a blemish on it you shall not bring, for it will not be accepted favorably for you.");
insert into verses values (57,  3, 22, 21, 'ואיש כי-יקריב זבח-שלמים ליקוק לפלא-נדר או לנדבה בבקר או בצאן תמים יהיה לרצון כל-מום לא יהיה-בו', "If a man will bring a peace-offering to Adonoy, to fulfill an articulated vow or free-will-offering of cattle or of sheep, it shall be unblemished, that it be favorably accepted. No blemish shall be in it.");
insert into verses values (300, 3, 22, 22, 'עורת או שבור או-חורץ או-יבלת או גרב או ילפת לא-תקריבו אלה ליקוק ואשה לא-תתנו מהם על-המזבח ליקוק', "A blind, broken-limbed, cracked or wart[ed animal]; or [an animal] with scurf or scabs you shall not bring these to Adonoy; and as a fire-offering you shall not place of them on the altar of Adonoy.");
insert into verses values (301, 3, 22, 24, 'ומעוך וכתות ונתוק וכרות לא תקריבו ליקוק ובארצכם לא תעשו', "[An animal that has its testicles] crushed, mangled, torn or cut off you shall not bring to Adonoy and in your land you shall not inflict these injuries.");
insert into verses values (303, 3, 22, 25, 'ומיד בן-נכר לא תקריבו את-לחם אלקיכם מכל-אלה כי משחתם בהם מום בם לא ירצו לכם', "And from a strangers [gentiles] hand you shall not bring (offer) the bread of your God from any of these for they have inflicted wounds in them; they will not be favorably accepted for you.");
insert into verses values (306, 3, 22, 28, 'ושור או-שה אתו ואת-בנו לא תשחטו ביום אחד', "An ox or a lamb, it and its offspring, you shall not slaughter in one day.");
insert into verses values (318, 3, 22, 30, 'ביום ההוא יאכל לא-תותירו ממנו עד-בקר אני יקוק', "On that day [that it is sacrificed] you should eat it; you shall leave none of it until the next morning, I am Adonoy.");
insert into verses values (7,   3, 22, 32, 'ולא תחללו את-שם קדשי ונקדשתי בתוך בני ישראל אני יקוק מקדשכם', "You shall not profane My holy Name; but I will be sanctified among Bnei Yisroel. I am Adonoy Who makes you holy.");
insert into verses values (40,  3, 23, 8,  'והקרבתם אשה ליקוק שבעת ימים ביום השביעי מקרא-קדש כל-מלאכת עבדה לא תעשו', "You shall bring a fire-offering to Adonoy [on each of] seven days. On the seventh day it is [a day] of holy assembly; you shall not do any work of labor.");
insert into verses values (41,  3, 23, 10, 'דבר אל-בני ישראל ואמרת אלהם כי-תבאו אל-הראץ אשר אני נתן לכם וקצרתם את-קצירה והבאתם את-עמר ראשית קצירכם אל-הכהן', "Speak to Bnei Yisroel and say to them; when you come into the land that I give to you and you reap its harvest, you shall bring an omer of the first fruits of your harvest to the kohein.");
insert into verses values (364, 3, 23, 14, 'ולחם וקלי וכרמל לא תאכלו עד-עצם היום הזה עד הביאכם את-קרבן אלקיכם חקת עולם לדרתיכם בכל משבתיכם', "Bread, parched grain or tender grain you shall not eat until this very day, until you bring the offering of your God; it is an everlasting statute for all your generations in all your dwelling places.");
insert into verses values (149, 3, 23, 15, 'וספרתם לכם ממחרת השבת מיום הביאכם את-עמר התנופה שבע שבתות תמימת תהיינה', "You shall count for yourselves, from the day after the day of rest (Pesach) from the day on which you will bring the omer waveuplifted-offering, seven complete weeks they shall be,");
insert into verses values (43,  3, 23, 17, 'ממושבתיכם תביאו לחם תנופה שתים שני עשרנים סלת תהיינה חמץ תאפינה בכורים ליקוק', "From [the land of] your dwelling places you shall bring two bread wave-offerings; of two tenths of flour they shall be. You shall bake them leavened, as first-fruit-offering to Adonoy.");
insert into verses values (150, 3, 23, 21, 'וקראתם בעצם היום הזה מקרא-קדש יהיה לכם כל-מלאכת עבדה לא תעשו חקת עולם בכל-מושבתיכם לדרתיכם', "You shall proclaim on this very day; it shall be a [day of] holy assembly for you you shall not do any work of labor. It is an everlasting statute in all your dwelling places throughout your generations.");
insert into verses values (151, 3, 23, 24, 'דבר אל-בני ישראל לאמר בחדש השביעי באדח לחדש יהיה לכם שבתון זכרון תרועה מקרא-קדש', "Speak to Bnei Yisroel, saying; in the seventh month, on the first of the month, shall be for you a [day of] rest. A remembrance of the sounding [of the shofor], a holy assembly.");
insert into verses values (447, 3, 23, 25, 'כל-מלאכת עבדה לא תעשו והקרבתם אשה ליקוק', "You shall not do any work of labor and you shall bring a fire-offering to Adonoy.");
insert into verses values (369, 3, 23, 29, 'כי כל-הנפש אשר לא-תענה בעצם היום הזה ונכרתה מעמיה', "For any person who will not be afflicted on this very day shall be cut off from his people.");
insert into verses values (154, 3, 23, 35, 'ביום הראשון מקרא-קדש כל-מלאכת עבודה לא תעשו', "On the first day [there shall be] a holy assembly; you shall not do any work of labor.");
insert into verses values (155, 3, 23, 36, 'שבעת ימים תקריבו אשה ליקוק ביום השמיני מקרא-קדש יהיה לכם והרקרבתם אשה ליקוק עצרת הוא כל-מלאכת עבדה לא תעשו', "For seven days, you shall bring a fire-offering to Adonoy; the eighth day shall be for you a holy assembly, and you shall bring a fire-offering to Adonoy. It is [a day] of convocation. you shall not do any work of labor.");
insert into verses values (157, 3, 23, 40, 'ולקחתם לכם ביום הראשון פרי עץ הדר כפת תמרים וענף עץ-עבת וערבי-נחל ושמחתם לפני יקוק אלקיכם שבעת ימים', "You shall take for yourselves, on the first day, the fruit of the beautiful tree (esrog), a branch of palm trees (lulav), boughs of thick-leaved trees (hadasim), and willows of the brook (aravos), and you shall rejoice before Adonoy, your God, for seven days.");
insert into verses values (156, 3, 23, 42, 'בסכת תשבו שבעת ימים כל-האזרח בישראל ישבו בסכת', "You shall dwell in the sukkos seven days, every native born Israelite shall dwell in the sukkos.");
insert into verses values (383, 3, 25, 4,  'ובשנה השביעת שבת שבתון יהיה לארץ שבת ליקוק שדך לא תזרע וכרמך לא תזמר', "But the seventh year shall be a Shabbos of rest for the land, a Shabbos for Adonoy, you shall not plant your field and you shall not prune your vineyard.");
insert into verses values (384, 3, 25, 5,  'את ספיח קצירך לא תקצור ואת-ענבי נזירך לא תבצר שנת שבתון יהיה לארץ', "[Even] crops that grew on their own [from the seeds of your previous] harvest you shall not reap, and the grapes of your untended vines you shall not gather; it shall be a year of complete rest for the land.");
insert into verses values (474, 3, 25, 6,  'והיתה שבת הארץ לכם לאכלה לך ולעבדך ולאמתך ולשכירך ולתושבך הגרים עמך', "The [produce] of the lands Shabbos[-year] shall be for yourselves, for food, for you, your servant and your maidservant, for your hired hand and your resident sojourner who reside with you.");
insert into verses values (131, 3, 25, 8,  'וספרת לך שבע שבתת שנים שבע שנים שבע פעמים והיו לך ימי שבע שבתת השנים תשע וארבעים שנה', "You shall count for yourself seven sabbatical years, seven years, seven times and it shall be for you, the days (period) of the seven sabbatical years, forty-nine years.");
insert into verses values (128, 3, 25, 9,  'והעברת שופר תרועה בחדש השבעי בעשור לחדש ביום הכפרים תעבירו שופר בכל-ארצכם', "You shall make a proclamation with the shofor on the tenth day of the seventh month. On Yom Kippur shall this shofor-proclamation be made. throughout all your land.");
insert into verses values (127, 3, 25, 10, 'וקדשתם את שנת החמשים שנה קראתם דרור בארץ לכל-ישביה יובל הוא תהיה לכם ושבתם איש אל-אחזתו ואיש אל-משפחתו תשבו', "You shall sanctify the year of the fiftieth year and proclaim freedom in the land for all of its inhabitants; it shall be for you a jubilee and each man shall return to his ancestral land and each man shall return to his family.");
insert into verses values (385, 3, 25, 11, 'יובל הוא שנת החמשים שנה תהיה לכם לא תזרעו ולא תקצרו את-ספיחיה ולא תבצרו את-נזריה', "It is a jubilee, [that] the year of the fiftieth year shall be for you. You shall not plant and you shall not reap [even] crops that grew on their own; and you shall not gather the [grapes of] its untended vineyard,");
insert into verses values (230, 3, 25, 14, 'וכי-תמכרו ממכר לעמיתך או קנה מיד עמיתך אל-תונו איש את-אחיו', "If you sell anything to your neighbor, or purchase [something] from your neighbor, do not cheat one another.");
insert into verses values (403, 3, 25, 17, 'ולא תונו איש את-עמיתו ויראת מאלקיך כי אני יקוק אלקיכם', "You shall not cheat one another, and you shall fear your God for I am Adonoy, your God.");
insert into verses values (386, 3, 25, 23, 'והארץ לא תמכר לצמתת כי-לי הארץ כי-גרים ותשבים אתם עמדי', "The land shall not be sold in perpetuity, for the land is Mine, for, you are [merely as] foreigners and resident aliens before Me.");
insert into verses values (129, 3, 25, 24, 'ובכל ארץ אחזתכם גאלה תתמו לארץ', "In all of your ancestral lands you shall grant redemption to the land.");
insert into verses values (130, 3, 25, 29, 'ואיש כי-ימכר בית-מושב עיר חומה והיתה געלתו עד-תם שנת ממכרו ימים תהיה גאלתו', "If a man shall sell a residential home [that is] within a walled city, the time allotted for its redemption shall be until the end of one year after he sold it. He has the days [of a full year] in which to redeem it.");
insert into verses values (387, 3, 25, 34, 'ושדה מגרש עריהם לא ימכר כי-אחזת עולם הוא להם', "The field on the outskirts of their cities may not be sold for it is their perpetual ancestral land.");
insert into verses values (484, 3, 25, 35, 'וכי-ימוך אחיך ומטה ידו עמך והחזקת בו גר ותושב וחי עמך', "If your brother becomes impoverished and his hand (assets) become shaky among you, you must support him, [so too] the proselyte and the sojourner, [so] that he shall live with you.");
insert into verses values (393, 3, 25, 36, 'את-כספך לא-תתן לו בנשך ובמרבית לא-תתן אכלך', "You shall not take from him interest or usury and you shall fear your God, and your brother shall live with you.");
insert into verses values (408, 3, 25, 39, 'וכי-ימוך אחיך עמך ונמכר-לך לא-תעבד בו עבדת עבד', "If your brother becomes impoverished and is sold to you, you must not work him like a slave.");
insert into verses values (409, 3, 25, 42, 'כי-עבדי הם אשר-הוצאתי אתם מארץ מצרים לא ימכרו ממכרת עבד', "For they are My servants, whom I brought out from the land of Egypt, they shall not be sold [in the market] as slaves.");
insert into verses values (410, 3, 25, 43, 'לא-תרדה בו בפרך ויראת מאלקיך', "You must not rule over him to crush him and you shall fear your God.");
insert into verses values (220, 3, 25, 46, 'והתנחלתם אתם לבניכם אחריכם לרשת אחזה לעלם בהם תעבדו ובאחיכם בני-ישראל איש באחיו לא-תרדה בו בפרך', "You shall will them as inheritance to your children after you as hereditary property; you shall keep them in servitude permanently. However regarding your bretheren, Bnei Yisroel, man over his brother, you must not rule over him to crush him.");
insert into verses values (411, 3, 25, 53, 'כשכיר שנה בשנה יהיה עמו לא-ירדנו בפרך לעיניך', "As a hired hand [working] on a yearly basis shall he be considered with him; he shall not rule over him to crush him before your eyes.");
insert into verses values (242, 3, 26, 1,  'לא-תעשו לכם אלילם ופסל ומצבה לא-תקימו לכם ואבן משכית לא תתנו בארצכם להשתחות עליה כי אני יקוק אלקיכם', "You shall not make idols for yourselves and graven images and memorial stones you shall not raise up for youselves, and a prostration stone you shall not set in your land upon which to prostrate oneself, for I am Adonoy, your God.");
insert into verses values (109, 3, 27, 2,  'דבר אל-בני ישראל ואמרת אלהם איש כי יפלא נדר בערכך נפשת ליקוק', "Speak to Bnei Yisroel and say to them; when a person [orally] expresses a vow pledging valuation of souls, to Adonoy.");
insert into verses values (83,  3, 27, 10, 'לא יחלפינו ולא-ימיר אתו טוב ברע או-רע בטוב ואם-המר ימיר בהמה בבהמה והיה-הוא ותמורתו יהיה-קדש', "He shall not exchange it [with anothers animal] and he shall not change it [with his own animal] whether it be a good one for a worse one [or] a bad one for a better one. If he did exchange one animal for another animal, then both it and its replacement shall be sacred.");
insert into verses values (110, 3, 27, 11, 'ואם כל-בהמה טמאה אשר לא-יקריבו ממנה קרבן ליקוק והעמיד את-הבהמה לפני הכהן', "If it is any unclean (blemished) animal which cannot be offered to Adonoy, he shall present the animal to the kohein.");
insert into verses values (111, 3, 27, 14, 'ואיש כי-יקדש את-ביתו קדש ליקוק והעריכו הכהן בין טוב ובין רע כאשר יעריך אתו הכהן כן יקום', "If a man will consecrate his house as being sacred to Adonoy, the kohein shall determine its value, whether it is good or bad; as the kohein determines its value, so shall it be established.");
insert into verses values (112, 3, 27, 16, 'ואם משדה אחזתו יקדיש איש ליקוק והיה ערכך לפי זרעו זרע חמר שערים בחמשים שקל כסף', "If from his ancestral field a man will consecrate to Adonoy its valuation shall be according to the measure of its seed; [at the rate of] one chomer of barley seed per fifty silver shekalim.");
insert into verses values (310, 3, 27, 20, 'ואם-לט יגאל את-השדה ואם-מכר את-השדה לאיש אחד לא יגאל עוד', "If he does not redeem the field and if he sells the field to another person, it shall not longer be redeemable.");
insert into verses values (134, 3, 27, 28, 'אך-כל-חרם אשר יחרם איש ליקוק מכל-אשר-לו מאדם ובהמה ומשדה אחזתו לא ימכר ולא יגאל כל-חרם קדש-קדשים הוא ליקוק', "However, any dedication that a man will dedicate to Adonoy from any of his belongings be it people (slave), or animal, or of the field of his ancestral land, [none of these] can be sold or redeemed. Everything that is dedicated is most holy, unto Adonoy.");
insert into verses values (118, 3, 27, 30, 'וכל-מעשר הארץ מזרע הארץ מפרי העץ ליקוק הוא קדש ליקוק', "All tithings of the land; whether of the seeds [crops] of the land or of the fruit of the tree, belongs to Adonoy, it is sacred to Adonoy.");
insert into verses values (74,  3, 27, 32, 'וכל-מעשר בקר וצאן כל אשר-יעבר תחת השבט העשירי יהיה-קדש ליקוק', "All tithes of cattle and sheep, anything that passes under the rod, the tenth one shall be sacred to Adonoy.");
insert into verses values (309, 3, 27, 33, 'לא יבקר בין-טוב לרע ולא ימירנו ואם-המר ימירנו והיה-הוא ותמורתו יהיה-קדש לא יגאל', "He shall not make a distinction between better or worse [animals] and he shall not exchange it. If he exchanges one for another, then [both] it and its replacement shall be sacred and it cannot be redeemed.");
insert into verses values (505, 4, 5,  20, 'ולא-יבאו לראות בכלע את-הקדש ומתו', "[But] you, if you have gone astray while you were married to your husband, and if you have been defiled, and if a man has lain conjugally with you, [a man] other than your husband.");
insert into verses values (28,  4, 5,  2,  'צו את-בני ישארל וישלחו מן-המחנה כל-צרוע וכל-זב וכל טמא לנפש', "Command Bnei Yisroel that they must expel from the camp. anyone with tzora’as, and anyone from whom flows a [seminal] discharge and anyone who became defiled by a (departed) soul.");
insert into verses values (287, 4, 5,  3,  'מזכר עד-נקבה תשלחו אל-מחוץ למחנה משלחום ולא יטמאו את-מחניהם אשר אני שכן בתוכם', "Whether male or female you shall send them out. Beyond the camp you shall send them, and they shall not defile their camps in which I dwell among them.");
insert into verses values (69,  4, 5,  7,  'והתודו את-חטאתם אשר עשו והשיב את-אשמו בראשו וחמישתו יסף עליו ונתן לאשר אשם לו', "They shall confess the sins which they committed and return the principal amount [of the object] of his guilt, and add one fifth to that amount. He shall then give it to whom he is indebted.");
insert into verses values (307, 4, 5,  15, 'והביא האיש את-אשתו אל-הכהן והביא את-קרבנה עליה עשירת האיפה קמח שערים לא-יצק עליו שמן ולא-יתן עליו לבנה כי-מנחת קנאת הוא מנחת זכרון מזכרת עון', "that man shall bring his wife to the kohein and he shall bring her offering for her; [which is] one tenth of an epha of barley flour; he shall not pour oil over it and he shall not put frankincense on it, for it is a meal-offering of jealousy, a meal-offering of remembrance, recalling an iniquity.");
insert into verses values (209, 4, 5,  30, 'או איש אשר תעבר עליו רוח קנאה וקנא את-אשתו והעמיד את-האשה לפני יקוק ועשה לה הכהן את כל-התורה הזאת', "If a man is overcome with a spirit of jealousy and he is jealous of his wife, then he shall have the woman stand before Adonoy, and the kohein shall do unto her all of these laws.");
insert into verses values (375, 4, 6,  3,  'מיין ושכר יזיר חמץ יין וחמץ שכר לא ישתה וכל-משרת ענבים לא ישתה וענבים לחים ויבשים לא יאכל', "From new or old [intoxicating] wine, he must abstain. Vinegar made from new wine and vinegar made from old wine he shall not drink; anything steeped in grapes he shall not drink; moist grapes or dried grapes (raisins) he shall not eat.");
insert into verses values (376, 4, 6,  4,  'כל ימי נזרו מכל אשר יעשה מגפן היין מחרצנים ועד-זג לא יאכל', "As long as he is a Nazir from anything made of the grape-vine, from seeds to skin, he shall not eat.");
insert into verses values (88,  4, 6,  5,  'כל-ימי נדר נזרו תער לא-יעבר על-ראשו עד-מלאת הימם אשר-יזיר ליקוק קדש יהיה גדל פרע שער ראשו', "As long as he is under his nazirite oath a razor shall not pass over [to touch] his head. Until he completes his days as a Nazir to Adonoy he shall be sacred, he shall let the hair of his head grow long.");
insert into verses values (378, 4, 6,  6,  'כל-ימי הזירו ליקוק על-נפש מת לא יבא', "All the days that he is a Nazir to Adonoy, he shall have no contact with the dead.");
insert into verses values (377, 4, 6,  7,  'לאביו ולאמו לאחיו ולאחתו לא-יטמא להם במתם כי נזר אלקיו על-ראשו', "For his father, mother, brother and sister he shall not [ritually] defile himself for them when they die. For, the nazirite crown of his God is upon his head.");
insert into verses values (89,  4, 6,  9,  'וכי-ימות מת עליו בפתע פתאם וטמא ראש נזרו וגלח ראשו ביום טהרתו ביום השביעי יגלחנו', "If someone died near him suddenly, or unexpectedly, and caused his nazirite crowned head to be defiled, he shall shave his head on the day of his purification, he shall shave it on the seventh day.");
insert into verses values (23,  4, 6,  23, 'דבר אל-רהרן ואל-בניו לאמר כה תברכו את-בני ישראל אמור להם', "Speak to Aharon and to his sons, saying; This is how you shall bless Bnei Yisroel, saying to them:");
insert into verses values (31,  4, 7,  9,  'ולבני קהת לא נתן כי-עבדת הקדש עלהם בכתף ישאו', "He did not give any to the sons of Kehos because the sacred work was [incumbent] upon them, which they had to carry on their shoulders.");
insert into verses values (506, 4, 8,  25, 'ומבן חמשים שנה ישוב מצבא העבודה ולא יעבד עוד', "From the age of fifty he shall retire from the work force, and shall no longer work.");
insert into verses values (54,  4, 9,  11, 'בחדש השני בארבעה עשר יום בין הערבים יעשו אתו על-מצות ומררים יאכלהו', "in the second month, on the fourteenth day in the afternoon he shall perform it. Together with matzohs and bitter herbs they shall eat it.");
insert into verses values (317, 4, 9,  12, 'לא-ישאירו ממנו עד-בקר ועצם לא ישברו-בו ככל-חקת הפסח יעשו אתו', "They shall not leave any of it over until the morning and they shall not break any of its bones; according to all the statutes of the Pesach-offering they shall perform it.");
insert into verses values (55,  4, 10, 10, 'וביום שמלתכם ובמועדיכם ובראשי חדשיכם ותקעתם בחצצרת על עלתיכם ועל זבחי שלמיכם והיו לכם לזכרון לפני אלקיכם אני יקוק אלקיכם', "On your joyous days and on your festivals and on the beginnings of your months you shall blow with the trumpets for your burnt-offerings and for your peace-offerings; and it shall be a remembrance for you before your God, I am Adonoy your God.");
insert into verses values (124, 4, 15, 20, 'ראשית ערסתכם חלה תרימו תרומה כתרומת גרן כן תרימו אתה', "[From] the first portion of your doughs you shall separate a loaf as a separated portion; like the terumah-gift of the granary, so shall you separate it.");
insert into verses values (486, 4, 15, 37, 'מראשית ערסיכם תתנו ליקוק תרומה לדרתיכם', "And Adonoy spoke to Moshe, saying:");
insert into verses values (11,  4, 15, 38, 'דבר אל-בני ישראל ואמרת אלהם ועשו להם ציצת על-כנפי בגדיהם לדרתם ונתנו על-ציצת הכנף פתיל תכלת', "Speak to Bnei Yisroel and tell them to make for themselves, tzizis-fringes on the corners of their garments—throughout their generations; and they will place with the fringes of each corner a thread of blue.");
insert into verses values (264, 4, 15, 39, 'והיה לכם לציצת וראיתם אתו וזכרתם את-כל-מצות יקוק ועשיתם אתם ולא-תתרו אחרי לבבכם ואחרי עיניכם אשר-אתם זנים אחריהם', "And it will be to you for fringes and you will look upon it and you will remember all of Adonoy’s commandments and you will perform them; and you will not search after [the desire of] your hearts and after your eyes which cause you to go astray.");
insert into verses values (509, 4, 17, 5,  'זכרון לבני ישראל למען אשר לא-יקרב איש זר אשר לא מזרע אהרן הוא להקטיר קטרת לפני יקוק ולא-יהיה כקרח וכעדתו כאשר דבר יקוק ביד-משה זו', "to be a reminder for Bnei Yisroel so that a commoner shall never offer, one who is not a descendant of Aharon, to burn incense before Adonoy; so as not to be like Korach and his congregation; as Adonoy spoke to him, through Moshe.");
insert into verses values (19,  4, 18, 2,  'וגם את-אחיך מטה לוי שבט אביך הקרב אתך וילוו עליך וישמרתוך ואתה ובינך אתך לפני אהל העדת', "Also, your brothers, the tribe of Levi—the tribe of your father—bring to you, and they shall join you, and serve you, you and your sons with you, before the Tent of Testimony.");
insert into verses values (282, 4, 18, 3,  'ושמרו משמרתך ומשמרת כל-אהל אך אל-כלי הקדש ואל-המזבח לא יקרבו ולא-ימתו גם-הם גם-אתם', "They shall guard their duties to you and all the duties of the Tent, but to the holy furniture or to the Altar they must not approach, that neither they, nor you, shall die.");
insert into verses values (284, 4, 18, 4,  'ונלוו עליך ושמרו את-משמרת אהל מועד לכל עבדת האהל וזר לא-יקרב אליכם', "They shall join you and guard the duties of the Tent of Meeting—all the service of the Tent, but a commoner, [a non-kohein] should not approach you.");
insert into verses values (278, 4, 18, 5,  'ושמרתם את משמרת הקדש ואת משמרת המזבח ולא-יהיה עוד קצף על-בני ישראל', "You shall discharge the sacred duties and the duties of the Altar, so that there will be no more wrath on Bnei Yisroel.");
insert into verses values (473, 4, 18, 7,  'ואבה ובניך אתך תשמרו את-כהנתכם לכל-דבר המזבח ולמבית לפרכת ועבדתם עבדת מתנה אתן את-כהנתכם והזר הקרב יומת', "You and your sons with you shall guard your kehunah [duties] regarding the Altar and all that is within the Curtain—you shall serve—the service, as a gift, I give the kehunah to you, and the commoner who approaches shall be killed.”");
insert into verses values (308, 4, 18, 17, 'אך בכור-שור או-בכור כשב או-בכור עז לא תפדה קדש הם את-דמם תזרק על-המזבח ואת-חלבם תקטיר אשה לריח ניחח ליקוק', "However, a firstborn ox, a firstborn sheep or firstborn goat do not redeem because they are holy; sprinkle their blood on the altar and burn their fats as a fire-offering as a pleasing fragrance to Adonoy.");
insert into verses values (20,  4, 18, 23, 'ועבד הלוי הוא את-עבדת אהל מועד והם ישאו עונם חקת עולם לגרתיכם ובתוך בני ישראל לא ינחלו נחלה', "The Levi[tes]—[they] shall perform the service of the Tent of Meeting and they will bear their guilt; it is an everlasting decree—throughout your generations—among Bnei Yisroel they (the Levites) shall have no inheritance.");
insert into verses values (120, 4, 18, 26, 'ואל-הלוים תדבר ואמרת אלהם כי-תקחו מאת בני-ישראל את-המעשר אשר נתתי לכם מאתם בנחלתכם והרמתם ממנו תרומת יקוק מעשר מן-המעשר', "“Speak to the Levites and say to them: ‘When you take the tithe from Bnei Yisroel, which I gave you from them for your inheritance, set aside from it the terumah-gift of Adonoy, a tithe of the tithe.");
insert into verses values (479, 4, 18, 29, 'מכל מתנתיכם תרימו את כל-תרומת יקוק מכל-חלבו את-מקדשו ממנו', "From all your gifts you must set aside all of the terumah-gifts of Adonoy; from each thing—its finest part—the part that is to be consecrated.’");
insert into verses values (497, 4, 18, 32, 'ולא-תשאו עליו חטא בהרימכם את-חלבו ממנו ואת-קדשי בני-ישראל לא תחללו ולא תמותו', "You will not bear a sin because of it, when you separate its best part; but sacred things of Bnei Yisroel you must not profane, and you will not die.”’");
insert into verses values (108, 4, 19, 9,  'ואסף איש טהור את אפר הפרה והניח מחוץ למחנה במקום טהור והיתה לעדת בני-ישראל למשמרת למי נדה חטאת הוא', "A ritually clean person shall gather the cow’s ashes and place [it] outside the camp in a clean place; it shall remain a keepsake for the community of Bnei Yisroel for sprinkling water for purification.");
insert into verses values (103, 4, 19, 11, 'הנגע במת לכל-נפש אדם וטמא שבעת ימים', "One who touches the corpse of any [human] soul shall become unclean for seven days.");
insert into verses values (104, 4, 19, 19, 'והזה הטהר על-הטמא ביום השלישי והיום השביעי וחטאו ביום השביעי וכבס בגדיו ורחץ במים וטהר בערב', "The ritually clean person shall sprinkle upon the unclean person on the third day and on the seventh day; he shall purify him on the seventh day when he must wash his garments and bathe in water, and then he becomes clean in the evening.");
insert into verses values (56,  4, 22, 27, 'שור אן-כשב או-עז כי יולד והיה שבעת ימים תחת אמו ומיום השמיני והלאה ירצה לקרבן אשה ליקוק', "When the donkey saw the angel of Adonoy, it crouched beneath Bil’am; Bil’am became angry and beat the donkey with the stick.");
insert into verses values (233, 4, 27, 8,  'ואל-בני ישראל תדבר לאמר איש כי-ימות ובן אין לו והעברתם את-נחלתו לבתו', "Speak to Bnei Yisroel and tell them, that if a man dies and has no son, his hereditary property shall be transferred to his daughter.");
insert into verses values (36,  4, 28, 3,  'ואמרת להם זה האשה אשר תקריבו ליקוק כבשים בני-שנה תמימם שנים ליום עלה תמיד', "Say to them: “This is the fire-offering that you shall offer to Adonoy; yearling lambs without blemish, two each day, as a constant (daily) burnt-offering.");
insert into verses values (38,  4, 28, 9,  'וביום השבת שני-כבשים בני-שנה תמימם ושני עשרנים סלת מנחה בלולה בשמן ונסכו', "On the Shabbos day [the offering shall be] two yearling lambs without blemish, and two tenths [of an eiphah] of fine flour as a meal-offering, mixed with [olive] oil, and its libation.");
insert into verses values (39,  4, 28, 11, 'ובראשי חדשיכם תקריבו עלה ליקוק פרים בני-בקר שנים ואיל אחד כבשים בני-שנה שבעה תמימם', "At the beginning of your months you shall bring a burnt-offering to Adonoy, two young bulls, one ram, seven yearling lambs, [all] without blemish.");
insert into verses values (42,  4, 28, 26, 'וביום הבכורים בהקריבכם מנחה חדשה ליקוק בשבעתיכם מקרא-קדש יהיה לכם כל-מלאכת עבדה לא תעשו', "On the day of the first fruits when you bring a new meal-offering to Adonoy on your Festival of Weeks, there shall be a sacred holiday to you when you shall not do any work of consequence.");
insert into verses values (158, 4, 29, 1,  'ובחש השביעי באחד לחדש מקרא-קדש יהיה לכם כל-מלאכת עבדה לא תעשו יום תרועה יהיה לכם', "The first day of the seventh month shall be a sacred holiday to you when you shall not do any work of consequence. It shall be a day of sounding the ram’s horn.");
insert into verses values (44,  4, 29, 2,  'ועשיתם עלה לריח ניחח ליקוק פר בן-בקר אחד איל אחד כבשים בני-שנה שבעה תמימם', "You shall bring a burnt-offering of a pleasing aroma to Adonoy, [consisting of] one young bull, one ram, and seven yearling lambs, [all] without blemish.");
insert into verses values (448, 4, 29, 7,  'ובעשור לחדש השביעי הזה מקרא-קדש יהיה לכם ועניתם את-נפשתיכם כל-מלאכה לא תעשו', "On the tenth day of this seventh month there shall be a sacred holiday to you, when you shall fast and not do any work.");
insert into verses values (45,  4, 29, 8,  'והקרבתם עלה ליקוק ריח ניחח פר בן-בקר אחד איל אחד כבשים בני-שנה שבעה תמימם יהיו לכם', "You shall bring a burnt-offering Adonoy, for a pleasing aroma, [consisting of] one young bull, one ram, and seven yearling lambs. They shall [all] be without blemish.");
insert into verses values (47,  4, 29, 13, 'והקרבתם עלה אשה ריח ניחח ליקוק פרים בני-בקר שלשה עשר אילם שנים כבשים בני-שנה ארבעה עשר תמימם יהיו', "You shall bring a burnt-offering, a fire-offering for a pleasing aroma to Adonoy, [consisting of] thirteen young bulls, two rams, and fourteen yearling lambs. They shall [all] be without blemish.");
insert into verses values (48,  4, 29, 36, 'והקרבתם עלה אשה ריח ניחח ליקוק פר אחד איל אחד כבשים בני-שנה שבעה תמימם', "You shall bring a burnt-offering, a fire-offering for a pleasing aroma to Adonoy, [consisting of] one young bull, one ram, and seven yearling lambs, [all] without blemish;");
insert into verses values (91,  4, 30, 2,  'וידבר משה אל-ראשי המטות לבני ישראל לאמר זה הדבר אשר צוה יקוק', "Moshe spoke to the tribal leaders of Bnei Yisroel saying: “This is the word that Adonoy has commanded.");
insert into verses values (338, 4, 30, 3,  'איש כי-ידר נדר ליקוק או-השבע שבעה לאסר אסר על-נפשו לא יחל דברו ככל-היצא מפיו יעשה', "If a man makes a vow to Adonoy or makes an oath; to initiate a prohibition himself; he may not profane his word. He shall do all that he said.");
insert into verses values (476, 4, 31, 7,  'ויצבאו על-מדין כאשר צוה יקוק את-משה ויהרגו כל-זכר', "They mounted a campaign against Midian, as Adonoy had commanded Moshe, and they killed every male.");
insert into verses values (475, 4, 33, 53, 'והורשתם את-הארץ וישבתם-בה כי לכם נתתי את-הארץ לרשת אתה', "Drive out [the inhabitants of] the land and settle in it, because I have given this land to you to possess.");
insert into verses values (170, 4, 35, 2,  'צו את-בני ישראל ונתנו ללוים מנחלת אחזתם ערים לשבת ומגרש לערים סביבתיהם תתנו ללוים', "“Instruct Bnei Yisroel and have them give to the Levites from their hereditary possession, cities in which to settle; and open space for the cities all around you shall give to the Levites.");
insert into verses values (430, 4, 35, 12, 'והיו לכם הערים למקלט מגאל ולא ימות הרצח עד-עמדו לפני העדה למשפט', "These cities shall serve you as a refuge from the avenger, so that the murderer will not die until he stands in judgment before the congregation.");
insert into verses values (482, 4, 35, 19, 'גאל הדם הוא ימית את-הרצח בפגעו-בו הוא ימיתנו', "The blood avenger shall kill the murderer, wherever he meets him he may kill him.");
insert into verses values (211, 4, 35, 25, 'והצילו העדה את-הרצח מיד גאל הדם והשיבו אתו העדה אל-עיר מקלטו אשר-נס שמה וישב בה עד-מות הכהן הגדל אשר-משח אתו בשמן הקדש', "The congregation shall protect the murderer from the blood avenger, and return him to his refuge city where he had fled, and he must remain there until the death of the Kohein Gadol who was anointed with the sacred oil.");
insert into verses values (429, 4, 35, 30, 'כל-מכה-נפש לפי עדים ירצח את-הרצח ועד אחד לא-יענה בנפש למות', "If anyone kills a person, only upon the testimony of witnesses shall the murderer be killed. But one witness may not testify against a person, in a case of capital punishment.");
insert into verses values (432, 4, 35, 31, 'ולא-תקחו כפר לנפש רצח אשר-הוא רשע למות כי-מות יומת', "And do not accept atonement compensation for the life of a murderer who is wicked to incur the death penalty, for he must be executed.");
insert into verses values (433, 4, 35, 32, 'ולא-תקחו כפר לנוס אל-עיר מקלטו לשוב לשבת בארץ עד-מות הכהן', "And do not accept atonement compensation for one who has fled to his refuge city, to allow him to return to live in the land, before the death of the Kohein (Gadol).");
insert into verses values (421, 5, 1,  17, 'לא-תכירו פנים במשפט כקטן כגדל תשמעון לא תגורו מפני-איש כי המשפט לאלקים הוא והדבר אשר יקשה מכם תקרבון אלי ושמעתיו', "Do not show favor in judgement; small and great alike will you hear out; do not fear any man; for justice belongs to God. And the matter that will be too difficult for you, present to me and I will hear it.’’");
insert into verses values (519, 5, 2,  5,  'אל-תתגרו בם כי לא-אתן לכם מארצם עד מדרך כף-רגל כי-ירשה לעשו נתתי את-הר שעיר', 'Do not incite them. For I will not give you of their land as much as a footstep, because for Eisov’s inheritance have I designated Mount Seir.');
insert into verses values (517, 5, 2,  9,  'ויאמר יקוק אלי אל-תצר את-מואב ואל-תתגר בם מלחמה כי לא-אתן לך מארצו ירשה כי לבני-לוט נתתי את-ער ירשה', 'Adonoy said to me, ‘‘Do not besiege Moav and do not intimidate them with war. For I will not give you [any] of his land as inheritance, for to Lot’s descendants have I given Or as inheritance.');
insert into verses values (518, 5, 2,  19, 'וקרבת מול בני עמון אל-תצרם ואל-תתגר בם כי לא-אתן מארץ בני-עמון לך ירשה כי לבני-לוט נתתיה ירשה', 'When you approach [the area] facing the descendants of Ammon, do not besiege them and do not intimidate them, for I will not give you of the land of the descendants of Ammon an inheritance, because to the descendants of Lot have I given it as an inheritance.');
insert into verses values (512, 5, 4,  6,  'ושמרתם ועשיתם כי הוא חכמתכם ובינתכם לעיני העמים אשר ישמעון את כל-החקים האלה ואמרו רק עם-חכם ונבון הגוי הגדול הזה', "You will preserve and you will fulfill [them], for that [displays] your wisdom and your understanding before the nations, who will hear all these statutes and will say, ‘‘The only wise and understanding people is this great nation.’’");
insert into verses values (494, 5, 4,  9,  'רק השמר לך ושמר נפשך מאד פן-תשכח את-הדברים אשר-ראו עיניך ופן-יסורו מלבבך כל ימי חייך והודעתם לבניך ולבני בניך', "Look out for yourself and guard your life exceedingly, lest you forget the words your eyes witnessed, and lest they are removed from your mind, all the days of your life; you will make them known to your children and to your grandchildren.");
insert into verses values (415, 5, 5,  18, 'ולא תחמד אשת רעך ולא תתאוה בית רעך שדהו ועבדו ואמתו שורו וחמרו וכל אשר לרעך', "And do not covet your neighbor’s wife. And do not desire your neighbor’s house, his field, or his male slave or his female slave, his ox or his donkey, or anything else that belongs to your neighbor.’’");
insert into verses values (2,   5, 6,  4,  'שמע ישראל יקוק אלקינו יקוק אחד', "Listen, Yisroel! Adonoy is our God, Adonoy is one.");
insert into verses values (3,   5, 6,  5,  'ואהבת את יקוק אלקיך בכל-לבבך ובכל-נפשך ובכל-מאדך', "You are to love Adonoy, your God, with all your heart, with all your soul, and with all your possessions.");
insert into verses values (8,   5, 6,  7,  'ושננתם לבניך ודברת בם בשבתך בביתך ובלכתך בדרך ובשכבך ובקומך', "You are to teach them to your children and you are to discuss them, when you sit at home, and when you journey on the road, and when you go to sleep, and when you rise.");
insert into verses values (10,  5, 6,  8,  'וקשרתם לאות על-ידך והיו לטטפת בין עיניך', "You are to tie them as a sign on your arm and they are to be totafos between your eyes.");
insert into verses values (12,  5, 6,  9,  'וכתבתם על-מזוזת ביתך ובשעריך', "You are to write them on the doorposts of your house, and on your gateposts.");
insert into verses values (493, 5, 6,  12, 'השמר לך פן-תשכח את-יקוק אשר הוציאך מארץ מצרים מבית עבדים', "Look out for yourself, lest you forget Adonoy, Who took you out of the land of Egypt, from the house of slavery.");
insert into verses values (4,   5, 6,  13, 'את-יקוק אלקיך תירא ואתו תעבד ובשמו תשבע', "Fear Adonoy, your God, and serve Him, and swear by His name.");
insert into verses values (277, 5, 6,  16, 'לא תנסו את-יקוק אלקיכם כאשר נסיתם במסה', "Do not test Adonoy, your God, as you tested at Massoh.");
insert into verses values (265, 5, 7,  2,  'ונתנם יקוק אלקיך לפניך והיתם החרם תחרים אתם לא-תכרת להם ברית ולא תחנם', "Adonoy, your God, will defeat them before you, and you will smite them. You must surely annihilate them. Do not make a treaty with them, and do not favor them.");
insert into verses values (268, 5, 7,  3,  'ולא תתחתן בם תבך לא-תתן לבנו ובתו לא-תקח לבנך', "Do not marry them; do not give your daughter to his son, nor take his daughter for your son.");
insert into verses values (273, 5, 7,  21, 'לא תערץ מפניהם כי-יקוק אלקיך בקרבך קל גדול ונורא', "Do not be intimidated by them, for Adonoy, your God, is amid you, the great and fearsome Almighty.");
insert into verses values (247, 5, 7,  25, 'פסילי אלהיהם תשרפון באש לא-תחמד כסף וזהב עליהם ולקחת לך פן תוקש בו כי תועבד יקוק אלקיך הוא', "Burn the statues of their gods in fire; do not covet the silver and gold upon them and take it for yourself, lest you be ensnared by it, because it is abominated by Adonoy, your God.");
insert into verses values (249, 5, 7,  26, 'ולא-תביא תועבה אל-ביתך והיית חרם כמהו שקץ תשקצנו ותעב תתעבנו כי-חרם הוא', "Do not bring abomination into your house [lest] you become cheirem like it; thoroughly revile and abominate it, for it is cheirem. ");
insert into verses values (511, 5, 8,  5,  'וידעת עם-לבבך כי כאשר ייסר איש את-בנו יקוק אלקיך מיסרך', "You will know with your heart that the way a man trains his son does Adonoy, your God, train you.");
insert into verses values (16,  5, 8,  10, 'ואכלת ושבעת וברכת את-יקוק אלקיך על-הראץ הטבה אשר נתן-לך', "You will eat and be full, and you will bless Adonoy, your God, for the good land He has given you.");
insert into verses values (514, 5, 8,  11, 'השמר לך פן-תשכח את-יקוק אלקיך לבלתי שמר מצותיו ומשפטיו וחקתיו אשר אנכי מצוך היום', "Look out for yourself lest you forget Adonoy, your God, to not guard His commandments, His laws, and His statutes that I am commanding you today.");
insert into verses values (194, 5, 10, 19, 'ואהבתם את-הגר כי-גרים הייתם בארץ מצרים', "You are to love the convert, because you were strangers in the land of Egypt.");
insert into verses values (9,   5, 10, 20, 'את-יקוק אלקיך תירא אתו תעבד ובו תדבק ובשמו תשבע', "You are to fear Adonoy, your God, serve Him, cling to Him, and swear by His name.");
insert into verses values (172, 5, 12, 2,  'אבד תאבדון את-כל-המקמות אשר עבדו-שם הגוים אשר אתם ירשים אתם את-אלהיהם על-ההרים הרמים ועל-הגבעות ותחת כל-עץ רענן', "You must obliterate all the places where the nations worshipped, whom you are inheriting, their gods, on the high mountains, and on the hills, and under every evergreen tree.");
insert into verses values (79,  5, 12, 5,  'כי אם-אל-המקום אשר-יבחר יקוק אלקיכם מכל-שבטיכם לשום את-שמו שם לשכנו תדרשו ובאת שמה', "Rather at the place that Adonoy, your God, chooses from all your tribes to set His Presence there, will you seek His Presence and come there.");
insert into verses values (297, 5, 12, 13, 'השמר לך פן-תעלה עלתיך בכל-מקום אשר תראה', "Take heed, lest you offer up your burnt-offerings anywhere you envision as proper.");
insert into verses values (80,  5, 12, 14, 'כי אם במקום אשר-יבחר יקוק באחד שבטיך שם תעלה עלתיך ושם תעשה כל אשר אנכי מצוך', "Solely in the place that Adonoy chooses within one of your tribes, there are you to offer up your burnt-offerings, and there are you to do everything that I am commanding you.");
insert into verses values (82,  5, 12, 15, 'רק בכל-אות נפשך תזבח ואכלת בשר בכרכת יקוק אלקיך אשר נתן-לך בכל-שעריך והטהור יאכלנו כצבה וכאיל', "Except with the fullness of your appetite you may slaughter and eat meat in accord with the blessing of Adonoy, your God, that He has given you in all your cities; the ritually unclean and the clean will eat it like the deer and the gazelle.");
insert into verses values (333, 5, 12, 17, 'לא-תוכל לאכל בשעריך מעשר דגנך ותירשך ויצהרך ובכרת בקרך וצאנך וכל-נדריך אשר תדר ונדבתיך ותרומת ידך', "You are not permitted to eat in your cities the tithe of your grain and your wine and your olive oil, and the first-born of your cattle and your flocks, and all your pledges that you pledge, and your donations and the terumah-separation of your hand-offerings.");
insert into verses values (388, 5, 12, 19, 'השמר לך פן-תעזב את-הלוי כל-ימיך על-אדמתך', "Watch yourself, lest you forsake the Levite, all your years on your land.");
insert into verses values (135, 5, 12, 21, 'כי-ירחק ממך המקום אשר יבחר יקוק אלקיך לשום שמו שם וזבחת מבקרך ומצאנך אשר נתן יקוק לך כאשר צויתך ואכלת בשעריך בכל אות נפשך', "When the place is distant from you that Adonoy, your God, chooses to set His Presence there, you may slaughter some of your cattle or your flocks that Adonoy gave you, as I have commanded you; and you will eat in your cities with all your appetite.");
insert into verses values (358, 5, 12, 23, 'רק חזק לבלתי אכל הדם כי הדם הוא הנפש ולא-תאכל הנפש עם-הבשר', "Only, exercise strength in not eating the blood, for the blood is the life; and do not eat the spirit with the meat.");
insert into verses values (81,  5, 12, 26, 'רק קדשך אשר-יהיו לך ונדריך תשע ובאת אל-המקום אשר-יבחר יקוק', "However, your sacred offerings that you will have, and your pledges, you are to bring when you come to the place that Adonoy chooses.");
insert into verses values (441, 5, 13, 1,  'את כל-הדבר אשר אנכי מצוה אתכם אתו תשמרו לעשות לא-תסף עליו ולא תגרע ממנו', "Everything that I am commanding you—be careful to fulfill it; do not add to it and do not subtract from it.");
insert into verses values (251, 5, 13, 4,  'לא תשמע אל-דברי הנביא ההוא או אל-חולם החלום ההוא כי מנסה יקוק אלקיכם אתכם לדעת הישכם אהבים את-יקוק אלקיכם בכל-לבבכם ובכל-נפשכם', "Do not listen to the words of that prophet or to that dreamer of a dream, because Adonoy, your God, is testing you to know whether you love Adonoy, your God, wholeheartedly and with your entire beings.");
insert into verses values (489, 5, 13, 6,  'והנביא ההוא או חלק החלום ההוא יומת כי דבר-סרה על-יקוק אלקיכם המוציא אתכם מארץ מצרים והפדך מבית עבדים להדיחך מן-הדרך אשר צוך יקוק אלקיך ללכת בה ובערת הרע מקרבך', "And that prophet or that dreamer of a dream is to be executed, for he has uttered a fabrication about Adonoy, your God—Who took you out of the land of Egypt, and Who redeemed you from the house of bondage—to mislead you from the way that Adonoy, your God, commanded you to go upon, and you will eliminate the evil from within you.");
insert into verses values (246, 5, 13, 9,  'לא-תאבה לו ולא תשמע אליו ולא-תחוס עינך ולא-תחמל ולא-תכסה עליו', "Do not be favorbly inclined towards him, and do not listen to him; and do not view him compassionately, and do not take pity and do not cover for him.");
insert into verses values (245, 5, 13, 12, 'וכל-ישראל ישמעו ויראון ולא-יוספו לעשות כדבר הרע הזה בקרבך', "And all of Yisroel will hear and will fear, and they will not proceed to do so evil a thing as this, within you.");
insert into verses values (166, 5, 13, 15, 'ודרשת וחקרת ושאלת היטב והנה אמת נכון הדבר נעשתה התועבה הזאת בקרבך', "You must investigate, inquire, and interrogate thoroughly; and if in fact the report is true and accurate—this abomination was committed among you.");
insert into verses values (173, 5, 13, 17, 'ואת-כל-שללה תקבץ אל-תוך רחבה ושרפת באש את-העיר ואת-כל-שללה כליל ליקוק אלקיך והיתה תל עולם לא תבנה עוד', "And all its booty are you to collect within its plaza, and you are to burn in fire the city and all its booty—totally—for Adonoy, your God; and it will be a ruin forever, it is never to be rebuilt.");
insert into verses values (248, 5, 13, 18, 'ולא-ידבק בידך מאומה מן-החרם למען ישוב יקוק מחרון אפו ונתן-לך רחמים ורחמך והרבך כאשר נשבע לאבתיך', "Let nothing of the cherem cling to your hand; so that Adonoy withdraws His raising fury and grant you mercy, and He will be merciful toward you and multiply you as He swore to your forefathers");
insert into verses values (347, 5, 14, 1,  'בנים אתם ליקוק אלקיכם לא תתגדדו ולא-שתימו קרחה בין עיניכם למת', "You are sons to Adonoy, your God; do not lacerate yourselves and do not make yourselves bald between your eyes for a dead person.");
insert into verses values (332, 5, 14, 3,  'לא תאכל כל-תועבה', "Do not eat any abomination.");
insert into verses values (139, 5, 14, 11, 'כל-צפור טהרה תאכלו', "You may eat any pure bird.");
insert into verses values (496, 5, 14, 12, 'וזה אשר לא-תאכלו מהם הנשר והפרס והעזניה', "And this is what you may not eat: the griffon vulture and the bearded vulture, and the black vulture;");
insert into verses values (351, 5, 14, 19, 'וכל שרץ העוף טמא הוא לכם לא יאכלו', "And all flying creeping creatures are ritually unclean for you; they may not be eaten.");
insert into verses values (356, 5, 14, 21, 'לא תאכלו כל-נבלה לגור אשר-בשעריך תתננה ואכלה או מכר לנכרי כי עם קדוש אתה ליקוק אלקיך לא-תבשל גדי בחלב אמו', "Do not eat any carcass; to the alien in your cities give it and he will eat it or sell it to a non-Jew, for you are a people sanctified to Adonoy, your God; do not cook a kid in its mother’s milk.");
insert into verses values (119, 5, 14, 22, 'עשר תעשר את כל-תבואת זרעך היצא השדה שנה שנה', "You must surely tithe all the produce of your planting, that your field yields on a yearly basis.");
insert into verses values (471, 5, 14, 23, 'ואכלת לפני יקוק אלקיך במקום אחשר-יבחר לכן שמו שם מעשר דגנך תירשך ויצהרך ובכרת בקרך וצאנך למען תלמד לריאה את-יקוק אלקיך כל-הימים', "You shall eat in the presence of Adonoy, your God, in the place He chooses to house His Presence there—the tithe of your grain, your wine, and your olive oil, and the firstborn of your cattle and flocks; in order that you learn to fear Adonoy, your God, all the years.");
insert into verses values (121, 5, 14, 28, 'מקצה שלש שנים תוציא את-כל-מעשר תבואתך בשנה ההוא והנחת בשעריך', "At the end of three years, separate all the tithes of your produce of that year and set them aside in your city.");
insert into verses values (389, 5, 15, 2,  'וזה דבר השמטה שמוט כל-בעל משה ידו אשר ישה ברעהו לא-יגש את-רעהו ואת-אחיו כי-קרא שמטה ליקוק', "And this is the statement of shemitah: suspend every creditor’s hand from his loan to his neighbor; he will not claim [it] from his neighbor or his brother because it was proclaimed shemitah to Adonoy.");
insert into verses values (132, 5, 15, 3,  'את-הנכרי תגש ואשר יהיה לך את-אחיך תשמט ידך', "You should demand payment from the stranger, but what you have with your brother your hand will relinquish.");
insert into verses values (391, 5, 15, 7,  'כי-יהיה בך אביון מאחד אחיך באחד שעריך בארצך אשר-יקוק אלקיך נתן לך לא תאמץ את-לבבך ולא תקפץ את-ידך מאחיך האביון', "If there should be someone destitute in your midst among one of your brothers in one of your cities, in your land that Adonoy, your God, is giving you; do not harden your heart and do not close your hand against your destitute brother.");
insert into verses values (182, 5, 15, 8,  'כי-פתח תפתח את-ידך לו והעבט תעביטנו די מחסרו אשר יחסר לו', "Rather, you must surely open your hand generously to him, and you must surely extend a loan to him to cover the wants which he lacks.");
insert into verses values (390, 5, 15, 9,  'השמר לך פן-יהיה דבר עם-לבבך בליעל לאמר קרבה שנת-השבע שנת השמטה ורעה עינך באחיך האביון ולא תתן לו וקרא עליך אל-יקוק והיה בך חטא', "Look out for yourself lest there be an evil thought in your mind, saying, ‘‘The seventh year is approaching, the shemitah year,’’ and you will look askance at your destitute brother and will not give him; and if he cries out concerning you to Adonoy, you will be regarded as sinful.");
insert into verses values (504, 5, 15, 10, 'נתון תתן לו ולא-ירע לבבך בתתך לו כי בגלל הדבר הזה יברכך יקוק אלקיך בכל-מעשך ובכל משלח ידך', "You must surely give him, and let your heart not hurt when you give him; for, as a consequence of this thing, Adonoy, your God, will bless you in all your work and in all your commerce.");
insert into verses values (392, 5, 15, 13, 'וכי-תשלחנו חפשי מעמך לא תשלחנו ריקם', "When you send him free from you, do not send him away empty-handed.");
insert into verses values (183, 5, 15, 14, 'העניק תעניק לו מצאנך ומגרנך ומיקבך אשר ברכך יקוק אלקיך תתן-לו', "You must surely present him with a severance bonus from your flocks, and from your threshing area, and your wine cellar, whatever Adonoy, your God, has blessed you with are you to give him.");
insert into verses values (503, 5, 15, 18, 'לא-יקשה בעינך בשלחך אתו חפשי מעמך כי משנה שכר שכיר עבדך שש שנים וברכך יקוק אלקיך בכל אשר תעשה', "Let it not seem difficult to you when you send him free from you, for double the profit produced by the wage earner has he worked for you six years; and Adonoy, your God, will bless you in everything you do.");
insert into verses values (312, 5, 15, 19, 'כל-הבכור אשר יולד בבקרך ובצאנך הזכר תקדיש ליקוק אלקיך לא תעבד בבכור שורך ולא תגז בכור צאנך', "Every firstborn that is born in your cattle, and in your flocks—a male—you must consecrate to Adonoy, your God; you may not work with your first-born ox, or shear the first-born of your flocks.");
insert into verses values (472, 5, 15, 22, 'בשעירך תאכלנו הטמא והטהור יחדו כצבי וכאיל', "In your cities may you eat it; the ritually unclean and the clean together, like the deer and the gazelle.");
insert into verses values (487, 5, 16, 1,  'שמור את-חדש האביב ועשית פסח ליקוק אלקיך כי בחדש האביב הוציאך יקוק אלקיך ממצרים לילה', "Take heed of the month of spring, when you will celebrate Pesach for Adonoy your God; for in the month of spring Adonoy, your God, took you out from Egypt at night.");
insert into verses values (372, 5, 16, 3,  'לא-תאכל עליו חמץ שבעת ימים תאכל-עליו מצות לחם עני כי בחפזון יצאת מארץ מצרים למען תזכר את-יום צאתך מארץ מצרים כל ימי חייך', "Do not eat chometz on it; seven days are you to eat on it matzos, bread of anguish; since in haste you left the land of Egypt, so that you remember the day of your exodus from the land of Egypt all the days of your life.");
insert into verses values (316, 5, 16, 4,  'ולא-יראה לך שאר בכל-גבלך שבעת ימים ולא-ילין מן-הבשר אשר תזבח בערב ביום הראשון לבקר', "And no sourdough of yours may be seen in all of your boundary seven days; and none of the flesh may remain overnight which you slaughtered towards the evening of the first day—until morning.");
insert into verses values (516, 5, 16, 5,  'לא תוכל לזבח את-הפסח באחד שעריך אשר-יקוק אלקיך נתן לך', "You are forbidden to slaughter the pesach in any of your cities that Adonoy, your God, is giving you.");
insert into verses values (51,  5, 16, 14, 'ושמחת בחגך אתה ובנך ובתך ועבדך ואמתך והלוי והגר והיתום והאלמנה אשר בשעריך', "You are to rejoice during your festival—you and your son and your daughter, and your male slave and your female slave, and the Levite and the proselyte, and the orphan and the widow who are in your city.");
insert into verses values (164, 5, 16, 18, 'שפטים ושטרים תתן-לך בכל-שעריך אשר יקוק אלקיך נתן לך לשבטיך ושפטו את-העם משפט-צדק', "Judges and police officers you shall appoint for yourself in all of your cities that Adonoy, your God, is giving you for your tribes; who will judge the people righteous justice.");
insert into verses values (243, 5, 16, 21, 'לא-תטע לך אשרה כל-עץ אצל מזבח יקוק אלקיך אשר תעשה-לך', "Do not plant an Asherah for yourself [or] any tree near the altar of Adonoy, your God, that you will make for yourself.");
insert into verses values (241, 5, 16, 22, 'ולא-תקים לך מצבה אשר שנא יקוק אלקיך', "And do not erect for yourself a monument that Adonoy, your God, detests.");
insert into verses values (302, 5, 17, 1,  'לא-תזבח ליקוק אלקיך שור ושה אשר יהיה בו מום כל דבר רע כי תועבת יקוק אלקיך הוא', "Do not sacrifice to Adonoy, your God, an ox or a lamb that has a blemish, any bad thing; for it is abominated by Adonoy, your God.");
insert into verses values (498, 5, 17, 6,  'על-פי שנים עדים או שלשה עדים יומת המת לא יומת על-פי עד אחד', "By the speech of two witnesses or three witnesses is the guilty one to be executed; he is not to be executed by the oral testimonoy of one witness.");
insert into verses values (162, 5, 17, 11, 'על-פי התורה אשר יורוך ועל-המשפט אשר-יאמרו לך תעשה לא תסור מן-הדבר אשר-יגידו לך ימין ושמאל', "In accord with the Torah that they instruct you and upon the law that they state to you, are you to act; do not deviate from the word they tell you, neither right or left.");
insert into verses values (161, 5, 17, 15, 'שום תשים עליך מלך אשר יבחר יקוק אלקיך בו מקרב אחיך תשים עליך מלך לא תוכל לתת עליך איש נכרי אשר לא-אחיך הוא', "Appoint are you to appoint over yourself a king whom Adonoy, your God, will choose. From among your brothers are you to appoint over yourself a king; you may not place over yourself a foreigner who is not your brother.");
insert into verses values (263, 5, 17, 16, 'רק לא-ירבה-לו סוסים ולא-ישיב את-העם מצרימה למען הרבות סוס ליקוק אמר לכם לא תספון בדרך הזה עוד', "However, he must not aquire an abundance of horses for himself so that he will not return the people to Egypt in order to aquire an abundance of horses, because Adonoy told you, ‘‘You are not to proceed to return along this route again.’’");
insert into verses values (470, 5, 17, 17, 'ולא ירבה-לו נשים ולא יסור לבבו וכסף וזהב לא ירבה-לו מאד', "And he is not to aquire an abundance of wives for himself so that his heart will not veer; and silver and gold he may not accumulate for himself in great abundance.");
insert into verses values (15,  5, 17, 18, 'והיה כבשתו על כסא ממלכתו וכתב לו את-משנה התורה הזאת על-ספר מלפני הכהנים הלוים', "It shall be, that when he occupies the throne of his kingdom, he must write for himself a duplicate of this Torah in a scroll form [the scroll] before the kohanim—the Levites.");
insert into verses values (346, 5, 18, 1,  'לא-יהיה לכהנים הלוים כל-שבט לוי חלק ונחלה עם-ישראל אשי יקוק ונחלתו יאכלון', "They will not have—the kohanim, the Levites, the entire tribe of Levi—a portion or an inheritance with Yisroel; Adonoy’s fire-offerings and His inheritance will they eat.");
insert into verses values (133, 5, 18, 3,  'וזה היה משפט הכהנים מאת העם מאת זבחי הזבח אם-שור אם-שה ונתן לכהן הזרע והלחיים והקבה', "And this will be the stipend of the kohanim from the people from the slaughterers of [permitted] meat, whether an ox or a sheep; he will give the kohein the foreleg and the jaw and the [fourth] stomach.");
insert into verses values (117, 5, 18, 4,  'ראשית דגנך תירשך ויצהרך וראשית גז צאנך תתן-לו', "The first portion of your grain, your wine, and your olive oil, and the first of the shearing of your sheep are you to give him.");
insert into verses values (33,  5, 18, 6,  'וכי-יבא הלוי מאחד שעריך מכל-ישראל אשר-הוא גר דם ובא בכל-אות נפשו אל-המקום אשר-יבחר יקוק', "If a Levite should come from one of your cities throughout Yisroel where he sojourns, coming will all his soul’s desire to the place that Adonoy chooses.");
insert into verses values (254, 5, 18, 10, 'לא-ימצא בך מעביר בנו-ובתו באש קסם קסמים מעונן ומנחש ומכשף', "Let there not exist among you anyone who passes his son or daughter through fire, who practices the kosem-occult; who practices time-frame-occult or who divines portentuous events or a sorcerer.");
insert into verses values (255, 5, 18, 11, 'וחבר חבר ושאל אוב וידעני ודרש אל-המתים', "Or a snake charmer, or one who invokes the spirit of Ov or yidoni, or communicates with the dead.");
insert into verses values (478, 5, 18, 13, 'תמים תהיה עם יקוק אלקיך', "Walk in perfect trust with Adonoy, your God.");
insert into verses values (160, 5, 18, 15, 'נביא מקרבך מאחיך כמני יקים לך יקוק אלקיך אליו תשמעון', "A prophet from your midst, of your brethren, like me, will Adonoy, your God, establish for you; heed him.");
insert into verses values (250, 5, 18, 20, 'אך הנביא אשר יזיד לדבר דבר בשמי את אשר לא-צויתיו לדבר ואשר ידבר בשם אלהים אחרים ומת הנביא ההוא', "But the prophet who will malevolently make a statement in My Name, something that I did not instruct him to say, or which he says in the name of foreign gods, that prophet must die.");
insert into verses values (252, 5, 18, 22, 'אשר ידבר הנביא בשם יקוק ולא-יהיה הדבר ולא יבוא הוא הדבר אשר לא-דברו יקוק בזגון דברו הנביא לא תגור ממנו', "Should the prophet speak in Adonoy’s Name, and the matter does not happen and is not fulfilled, that is the statement that Adonoy did not speak. The prophet spoke it malevolently; do not fear him.");
insert into verses values (169, 5, 19, 3,  'תכין לך הדרך ושלשת את-גבול ארצך אשר ינחילך יקוק אלקיך והיה לנוס שמה כל-רצח', "Ready the route for yourself and divide the borders of your land into three sections that Adonoy, your God, will allot you; this will serve for any murderer to flee there.");
insert into verses values (502, 5, 19, 13, 'לא-תחוס עינך עליו ובערת דם-הנקי מישראל וטוב לך', "Do not view him with compassion. You are to eliminate the [shedding of] innocent blood from Yisroel, and you will have it good.");
insert into verses values (402, 5, 19, 14, 'לא תסיג גבול רעך אשר גבלו ראשנים בנחלתך אשר תנחל בארץ אשר יקוק אלקיך נתן לך לרשתה', "Do not move back the boundary of your neighbor that the first [settlers] determine in your territory that you will inherit in the land that Adonoy, your God, is giving you to inherit.");
insert into verses values (427, 5, 19, 15, 'לא-יקום עד אחד באיש לכל-עון ולכל-חטאת בכל-חטא אשר יחטא על-פי שני עדים או על-פי שלשה-עדים יקום דבר', "One witness may not arise against a man about any sin or for any transgression or transgression that he transgresses; by the word of two witnesses or by the word of three witnesses let a matter be established.");
insert into verses values (167, 5, 19, 19, 'ועשיתם לו כאשר זמם לעשות לאחיו ובערת הרע מקרבך', "You are to do to [each of] them as he conspired to do to his brother, and eliminate the evil from among you;");
insert into verses values (424, 5, 19, 21, 'ולא תחוס עינך נפש בנפש עין בעין שן בשן יד ביד רגל ברגל', "You are not to have compassion: life for life, eye for eye, tooth for tooth, hand for hand, foot for foot.");
insert into verses values (178, 5, 20, 2,  'והיה כקרבכם אל-המלחמה ונגש הכהן ודבר אל-העם', "Now, as you near the battle, the kohein shall approach and speak to the people.");
insert into verses values (499, 5, 20, 8,  'ויספו השטרים לדבר אל-העם ואמרו מי-האיש הירא ורך הלבב ילך וישב לביתו ולא ימס את-לבב אחיו כלבבו', "The officers will further address the people and say. ‘‘Whoever is afraid or faint hearted, let him go and return home, and let him not destroy the resolve of his brothers like his own resolve.’’");
insert into verses values (177, 5, 20, 10, 'כי-תקרב אל-עיר להלחם עליה וקראת אליה לשלום', "When you near a city to do battle against it, you are to offer it peace.");
insert into verses values (266, 5, 20, 16, 'רק מערי העמים האלה אשר יקוק אלקיך נתן לך נחלה לא תחיה כל-נשמה', "However, from the cities of these peoples that Adonoy, your God, is giving you as inheritance, you are not to leave any person alive.");
insert into verses values (174, 5, 20, 17, 'כי-החרם תחרימם החתי והאמרי הכנעני והפרזי החוי והיבוסי כאשר צוך יקוק אלקיך', "Rather annihilate are you to annihilate them: the Chittites and the Emorites, the Canaanites and the Perizites, the Chivites and the Yevusites; as Adonoy, your God, commanded you.");
insert into verses values (272, 5, 20, 19, 'כי-תצור אל-עיר ימים רבים להלחם עליה לתפשה לא-תשחית את-עצה לנדח עליו גרזן כי ממנו תאכל ואתו לא תכרת כי האדם עץ השדה לבא מפניך במצור', "If you besiege a city many days to wage war against it, to capture it, do not harm [any of] its trees by chopping it with an ax, because you eat from it you are not to cut it down; For, is the tree in the field a man to join the besieged to escape you?");
insert into verses values (168, 5, 21, 4,  'והורדו זקני העיר ההוא את-העגלה אל-נחל איתן אשר לא-יעבד בו ולא יזרע וערפו-שם את-העגלה בנחל', "The elders of that city will take down the calf to a stony valley that is not to be tilled and not to be seeded, and they shall decapitate the calf in that valley.");
insert into verses values (510, 5, 21, 8,  'כפר לעמך ישראל אשר-פדית יקוק ואל-תתן דם נקי בקרב עמך ישראל ונכפר להם הדם', "[The kohanim will say,] ‘‘Forgive Your people Yisroel, whom You, Adonoy, have redeemed; and do not allow innocent blood [liability] within Your people Yisroel.’’ The blood shall thus be atoned for in their behalf.");
insert into verses values (208, 5, 21, 11, 'וראית בשביה אשת יפת-מאר וחשקת בה ולקחת לך לאשה', "And you see among the prisoners a beautifully formed woman; if you desire her, you may take her as your wife.");
insert into verses values (413, 5, 21, 14, 'והיה אם-לא חפצת בה ושלחתה לנפשה ומכר לא-תמכרנה בכסף לא-תתעמר בה תחת אשר עניתה', "But it shall come to pass, that if you do not desire her you will send her free, but to sell—you may not sell her for money; you may not exploit her since you have oppressed her.");
insert into verses values (501, 5, 21, 16, 'כי את-הבכר בן-השנואה יכיר לתת לו פי שנים בכל אשר-ימצא לו כי-הוא ראשית אנו לו משפט הבכרה', "When the day comes for him to will to his sons what he owns, he may not prefer the son of the loved one over the son of the hated one—the firstborn.");
insert into verses values (492, 5, 21, 17, 'כי את-הבכר בן-השנואה יכיר לתת לו פי שנים בכל אשר-ימצא לו כי-הוא ראשית אנו לו מפשט הבכרה', "Rather, the firstborn son of the hated one is he to recognize to give him doublefold of all that he possesses; for he is the first of his power, he has the rights of primogeniture.");
insert into verses values (216, 5, 21, 22, 'וכי-יהיה באיש חטא משפט-מות והומת ותלית אתו על-עץ', "If a man is guilty of a capital offense and is executed; you shall hang him from a tree.");
insert into verses values (217, 5, 21, 23, 'לא-תלין נבלתו על-העץ כי-קבור תקברנו ביום ההוא כי-קללת אלקים תלוי ולא תטמא את-אדמתך אשר יקוק אלקיך נתן לך נחלה', "Do not leave his corpse overnight on the tree, but you are surely to bury him on that day, for a hanging corpse is an affront to God; so do not defile your land that Adonoy, your God, is giving you as territory.");
insert into verses values (431, 5, 22, 26, 'ולנערה לא-תעשה דבר אין לנערה חטא מות כי כאשר יקום איש על-רעהו ורצחו נפש כן הדבר הזה', "But to the girl you will not do a thing, the girl has no death liability; for, just as if a man rises up against his peer and murders him, so is this matter.");
insert into verses values (191, 5, 22, 1,  'לא-תראה את-שור אחיך או את-שיו נדחים והתעלמת מהם השב תשיבם לאחיך', "You may not observe your brother’s ox or his sheep lost and conceal yourself from them; you must surely return them to your brother.");
insert into verses values (417, 5, 22, 3,  'וכן תעשה לחמרו וכן תעשה לשמלתו וכן תעשה לכל-אבדת אחיך אשר-תאבד ממנו ומצאתה לא תוכל להתעלם', "And so are you to do for his donkey, and so are you to do for his garment, and so are you to do for any lost object of your brother’s that is lost from him which you find. You may not conceal yourself.");
insert into verses values (190, 5, 22, 4,  'לא-תראה את-חמור אחיך או שורו נפלים בדרך והתעלמת מהם הקם תקים עמו', "Do not observe your brother’s donkey or his ox collapsing on the road and ignore them. You must surely lift it up with him.");
insert into verses values (257, 5, 22, 5,  'לא-יהיה כלי-דבר על-אשה ולא-ילבש דבר שמלת אשה כי תועבת יקוק אלקיך כל-עשה אלה', "A man’s attire may not be on a woman nor may a man wear a woman’s garment; for the abomination of Adonoy, your God, are all who do these [things].");
insert into verses values (437, 5, 22, 6,  'כי יקרא קן-צפור לפניך בדרך בכל-עץ או על-הארץ אפרחים או ביצים והאם רבצת על-האפרחים או על-הביצים לא-תקח אהם על-הבנים', "If you should chance upon a bird’s nest before you on the road in any tree, or on the ground, [with] fledglings or eggs, and the mother is sitting on the fledglings or on the eggs; do not take the mother with her offspring.");
insert into verses values (137, 5, 22, 7,  'שלח תשלח את-האם ואת-הבנים תקח-לך למען ייטב לך והארכת ימים', "You must surely send away the mother and the offspring take for yourself, so that you will benefit and you will live long.");
insert into verses values (171, 5, 22, 8,  'כי תבנה בית חדש ועשית מעקה לגגך ולא-תשים דמים בביתך כי-יפל הנפל ממנו', "When you build a new home, you are to make a fence for your roof; and do not place blood [liability] in your house, for someone who should fall may fall from it.");
insert into verses values (366, 5, 22, 9,  'לא-תזרע כרמך כלאים פן-תקדש המלאה הזרע אשר תזרע ותבואת הכרם', "Do not plant in your vineyard mixed species, lest the expansion become obnoxious—the seed that you plant—and the produce of the vineyard.");
insert into verses values (381, 5, 22, 10, 'לא-תחרש בשור-ובחמר יחדו', "You may not plow with an ox and with a donkey together.");
insert into verses values (259, 5, 22, 11, 'לא תלבש שעטנז צמר ופשתים יחדו', "Do not don a mixture of threads, wool and flax together.");
insert into verses values (206, 5, 22, 19, 'וענשו אתו מאה כסף ונתנו לאבי הנערה כי הוציא שם רע על בתולת ישראל ולו-תהיה לאשה לא-יוכל לשלחה כל-ימיו', "And they will fine him one hundred silver [shekels] and give [them] to the girl’s father, because he defamed a virgin of Yisroel; and she shall remain his wife, he may not divorce her all his life.");
insert into verses values (215, 5, 22, 24, 'והוצאתם את-שניהם אל-שער העיר ההוא וסקלתם אתם באבנים ומתו את-הנער על-דבר אשר לא-צעקה בעיר ואת-האיש על-דבר אשר ענה את-אשת רעהו ובערת הרע מקרבך', "You are to take them both out to the court of that city and stone them with stones to death, the girl for the fact that she did not scream in the city and the man for the fact that he oppressed the wife of his peer; and you will eliminate the evil within you.");
insert into verses values (205, 5, 22, 29, 'ונתן האיש השכב עמה לאבי הנער חמשים כסף ולו-תהיה לאשה תחת אשר ענה לא-יוכל שלחה כל-ימיו', "The man who had intercourse with her must give to the girl’s father fifty silver [shekels]; and she shall be his wife, since he oppressed her; he may not divorce her all his life.");
insert into verses values (469, 5, 23, 2,  'לא-יבא פצוע-דכה וכרות שפכה בקהל יקוק', "One may not enter—with injured or crushed genitals or with a gash causing spilling—into Adonoy’s community.");
insert into verses values (466, 5, 23, 3,  'לא-יבא ממזר בקהל יקוק גם דור עשירי לא-יבא לו בקהל יקוק', "A base-born may not enter into Adonoy’s community, even his tenth generation may not enter into Adonoy’s community.");
insert into verses values (269, 5, 23, 4,  'לא-יבא עמוני ומואבי בקהל יקוק גם דור עשירי לא-יבא להם בקהל יקוק עד-עולם', "[Neither] an Ammonite nor a Moavite may enter into Adonoy’s community; even their tenth generation may not enter into Adonoy’s community forever.");
insert into verses values (271, 5, 23, 7,  'לא-תדרש שלמם וטבתם כל-ימיך לעולם', "Do not pursue their peace and their benefit all your life forever.");
insert into verses values (270, 5, 23, 8,  'לא-תתעב אדמי כי אחיך הוא לא-תתעב מצרי כי-גר היית בארצו', "Do not despise the Edomite, for he is your brother; do not despise the Egyptian, for you were a stranger in his land.");
insert into verses values (500, 5, 23, 10, 'כי-תצא מחנה על-איביך ונשמרת מכל דבר רע', "When you go out to encamp against your enemies take precautions from every evil thing.");
insert into verses values (288, 5, 23, 11, 'כי-יהיה בך איש אשר לא-יהיה טהור מקרה-לילה ויצא אל-מחוץ למחנה לא יבא אל-תוך המחנה', "If there is a man among you who is not undefiled [because of] a nocturnal incident; he must go out of the camp, he may not enter into the midst of the camp.");
insert into verses values (179, 5, 23, 13, 'ויד תהיה לך מחוץ למחנה ויצאת שמה חוץ', "Have a designated place outside the camp, and you will go out there to excrete.");
insert into verses values (180, 5, 23, 14, 'ויתד תהיה לך על-אזנך והיה בשבתך חוץ וחפרתה בה ושבת וכסית את-צאתך', "And a peg are you to have in addition to your weapons; when you need to sit to excrete dig with it, then use it again to cover your excrement.");
insert into verses values (405, 5, 23, 16, 'לא-תסגיר עבד אל-אדניו אשר-ינצל אליך מעם אדניו', "Do not deliver a slave to his master, who escaped to you from his master.");
insert into verses values (406, 5, 23, 17, 'עמך ישב בקרבך במקום אשר-יבחר באחד שעריך בטוב לו לא תוננו', "With you will he live among you, in the place that he chooses in any of your cities, wherever it is good for him; do not distress him.");
insert into verses values (467, 5, 23, 18, 'לא-תהיה קדשה מבנות ישראל ולא-יהיה קדש מבני ישראל', "There is not to be a harlot from the daughters of Yisroel; and there shall not be a male prostitute from the sons of Yisroel.");
insert into verses values (305, 5, 23, 19, 'לא-תביא אתנן זונה ומחיר כלב בית יקוק אלקיך לכל-נדר תועבת יקוק אלקיך גם-שניהם', "Do not bring a harlot’s fee or payment for a dog to the house of Adonoy, your God, in fulfillment of any vow; because Adonoy, your God’s abominations are even both of them.");
insert into verses values (394, 5, 23, 20, 'לא-תשיך לאחיך נשך כסף נשך אכל נשך כל-דבר אשר ישך', "You may not pay usury to your brother, usury on money, usury on food; usury on any matter where it is paid.");
insert into verses values (185, 5, 23, 21, 'לנכרי תשיך ולאחיך לא תשיך למען יברכך יקוק אלקיך בכל מלשח ידך על-הארץ אשר-אתה בה-שמה לרשתה', "You may pay usury to the stranger, but to your brother do not pay usury; in order that Adonoy, your God, will bless you in all your commerce on the land that you are coming there to inherit.");
insert into verses values (336, 5, 23, 22, 'כי-תדר מדר ליקוק אלקיך לא תאחר לשלמו כי-דרש ידרשנו יקוק אלקיך מעמך והיה בך חטא', "If you make a vow to Adonoy, your God, do not delay in discharging it; for Adonoy, your God, will surely demand it from you, and you will be at fault.");
insert into verses values (90,  5, 23, 24, 'מוצא פשתיך תשמר ועית כאשר נדרת ליקוק אלקיך נדבה אשר דברת בפיך', "Keep what you have articulated; and perform whatever you vow; to Adonoy, your God, voluntarily—whatever you say with your mouth.");
insert into verses values (188, 5, 23, 25, 'כי תבא בכרם רעך ואכלת ענבים כנפשך שבעך ואל-כליך לא תתן', "When you enter your fellow’s vineyard, you may eat grapes as you desire, to your satisfaction; but you may not place it in your container.");
insert into verses values (416, 5, 23, 26, 'כי תבא בקמת רעך וקטפת מלילת בידך וחרמש לא תניף על קמת רעך', "When you enter your fellow’s standing grainstalks, you may cut off stalks by hand, but do not lift a sickle over your fellow’s standing grainstalks.");
insert into verses values (200, 5, 24, 1,  'כי-יקח איש אשה ובעלה והיה אם-לא תמצא-חן בעיניו כי-מצא בה ערות דבר וכתב לה ספר כריתות ונתן בידה ושלחה מביתו', "If a man marries a woman and consummates with her; should she not find favor in his eyes because he found in her something lecherous, he is to write for her a document of severance and place [it] in her hand and send her from his home.");
insert into verses values (468, 5, 24, 4,  'לא-יוכל בעלה הראשון אשר-שלחה לשוב לקחתה להיות לו לאשה אחרי אשר הטמאה כי-תועבה הוא לפני יקוק ולא תחטיא את-הארץ אשר יקוק אלקיך נתן לך נחלה', "Her first husband who divorced her may not remarry her to be his wife after her having been defiled, for it is an abomination before Adonoy; and you are not to make liable the land that Adonoy, your God, is giving you as territory.");
insert into verses values (201, 5, 24, 5,  'כי-יקח איש אשה חדשה לא יצא בצבא ולא-יעבר עליו לכל-דבר נקי יקוק לביתו שנה אחת ושמח את-אשתו אשר-לקח', "When a man marries a new wife, he is not to be mobilized into the army, it shall not impose itself over him in any matter; he shall be free for his home one year and he shall bring joy to the wife he married.");
insert into verses values (399, 5, 24, 6,  'לא-יחבל רחים ורכב כי-נפש הוא חבל', "He may not secure as collateral a mortar or pestle, for his is taking life as security.");
insert into verses values (439, 5, 24, 8,  'השמר בנגע-הצרעת לשמר מאד ולעשות ככל אשר-יורו אתכם הכהנים הלוים כאשר צויתם תשמרו לעשות', "Take precautions with the leprosy affliction to be extremely careful and to do—in accordance with everything that the kohanim the Levites shall instruct you; as I commanded them are you to make sure to do.");
insert into verses values (477, 5, 24, 9,  'זכור את אשר-עשה יקוק אלקיך למרים בדרך בצאתכם ממצרים', "Remember what Adonoy, your God, did to Miriam on the way when you were going out of Egypt.");
insert into verses values (396, 5, 24, 10, 'כי-תשה ברעך משאת מאומה לא-תבא אל-ביתו לעבט עבטו', "If you become your fellow’s creditor for any amount of debt, do not enter his house to take his pledge.");
insert into verses values (397, 5, 24, 12, 'ואם-איש עני הוא לא תשכב בעבטו', "And if he is a poor man, do not repose with his collateral [in your possession].");
insert into verses values (186, 5, 24, 13, 'השב תשיב לו את-העבוט כבא השמש ושכב בשלמתו אברכך ולך תהיה צדקה לפני יקוק אלקיך', "Return are you to return the pledge to him at sunset, and he will sleep with his garment and bless you; and it will be to your credit before Adonoy, your God.");
insert into verses values (187, 5, 24, 15, 'ביומו תתן שכרו ולא-תבוא עליו השמש כי עני הוא ואליו הוא נשא את-נפשו ולא יקרא עליך אל-יקוק והיה בך חטא', "On its day give his wage and let the sun not set upon it, for he is a poor man, and he places his life in jeopardy for it; [so that] he should not complain about you to Adonoy, and you will have a sin.");
insert into verses values (426, 5, 24, 16, 'לא-יומתו אבות על-בנים ובנים לא-יומתו על-אבות איש בחטאו יומתו', "Fathers shall not be executed through sons, and sons shall not be executed through fathers; each to be executed for his sin.");
insert into verses values (398, 5, 24, 17, 'לא תטה משפט גר יתום ולא תחבל בגד אלמנה', "Do not be devious in the judgment of the proselyte or orphan; and you may not secure as collateral, a widow’s garment.");
insert into verses values (115, 5, 24, 19, 'כי תקצר קצירך בשדך ושכחת עמר בשדה לא תשוב לקחתו לגר ליתום ולאלמנה יהיה למען יברכך יקוק אלקיך בכל מעשה ידיך', "When you reap your reaping in your field, and you forget a sheaf in the field, you may not return to take it; for the proselyte, for the orphan, and for the widow let it be; in order that Adonoy, your God, will bless you in all your endeavors.");
insert into verses values (210, 5, 25, 2,  'והיה אם-בן הכות הרשע והפילו השפט והכהו לפניו כדי רשעתו במספר', "Should the wicked one deserve flogging, the judge shall incline him and have him flogged before him in the amount befitting his crime, with the number near.");
insert into verses values (436, 5, 25, 3,  'ארבעים יכנו לא יסיף פן-יסיף להכתו על-אלה מכה רבה ונקלה אחיך לעיניך', "Forty is he to have him flogged, he may not add; lest he additionally flog him over these, a great flogging, when your brother will be slighted before you.");
insert into verses values (382, 5, 25, 4,  'לא-תחסם שור בדישו', "You may not muzzle an ox while it threshes.");
insert into verses values (203, 5, 25, 5,  'כי-ישבו אחים יחדו ומת אחד מהם ובן אין-לו לא-תהיה אשר-המת החוצה לאיש זר יבמה יבא עליה ולקחה לו לאשה ויבמה', "If brothers reside together, and one of them dies having no son, let the wife of the dead man not marry outside [the family] to a strange man; her brother-in-law will consummate with her thus marrying her to be his wife, and perform levirate marriage with her.");
insert into verses values (204, 5, 25, 9,  'ונגשה יבמתו אליו לעיני הזקנים וחלצה נעלו מעל רדלו וירקה בפניו וענתה ואמרה ככה יעשה לאיש אשר לא-יבנה את-בית אחיו', "And his sister-in-law will approach him in the sight of the judges, and she will remove his shoe from upon his foot, and spit before him; and she will say aloud, ‘‘This is done to the man who will not build his brother’s family.’’");
insert into verses values (232, 5, 25, 12, 'וקצתה את-כפה לא תחוס עינך', "You shall sever her hand; you are not to have compassion.");
insert into verses values (419, 5, 25, 14, 'לא-יהיה לך בביתך איפה ואיפה גדולה וקטנה', "You shall not have in your house varying measures, large and small.");
insert into verses values (176, 5, 25, 17, 'זכור את אשר-עשה לך עמלק בדרך בצאתכם ממצרים', "Remember what Amalek perpetrated against you on the way when you were going out of Egypt.");
insert into verses values (175, 5, 25, 19, 'והיה בהניח יקוק אלקיך לך מכל-איביך מסביב בארץ אשר יקוק-אלקיך נתן לך נחלה לרשתה תמחה את-זכר עמלק מתחת השמים לא תשכח', "When Adonoy, your God, has given you repose from all your enemies around, in the land that Adonoy, your God, is giving you as territory to inherit, you shall obliterate the memory of Amalek from beneath the sky; do not forget.");
insert into verses values (123, 5, 26, 5,  'וענית ואמרת לפני יקוק אלקיך ארמי אבד אבי וירד מצרימה ויגר שם במתי מעט ויהי-שם לגוי עצום ורב', "And you shall then proclaim and say before Adonoy, your God, ‘‘ the Aramite destroy[ed] my forefather, then he descended to Egypt and sojourned there with a tiny community; and there he became a great people, powerful and numerous.");
insert into verses values (485, 5, 26, 12, 'כי תכלה לעשר את-כל-מעשר תבואתך בשנה השלישת שנת המעשר ונתתה ללוי לגר ליתום ולאלמנה ואכלו בשעריך ושבעו', "When you finish tithing all the tithes of your produce in the third year, the year of the tithe; and you have given to the Levi, to the proselyte, to the orphan, and to the widow, so that they may eat within your portals to their satisfaction;");
insert into verses values (122, 5, 26, 13, 'ואמרת לפני יקוק אלקיך בערתי הקדש מן-הבית וגם נתתיו ללוי ולגר ליתום ולאלמנה ככל-מצותך אשר צויתני לא-עברתי ממצותיך ולא שכחתי', "You shall declare before Adonoy, your God, ‘‘I have divested my estate of sacred material, and I have also presented it to the Levi and also to the proselyte, the orphan, and the widow, totally according to Your command that You commanded me; I did not transgress any of Your commandments nor did I forget.");
insert into verses values (334, 5, 26, 14, 'לא-אכלתי באני ממנו ולא-בערתי ממנו בטמא ולא-נתתי ממנו למת שמעתי בקול יקוק אלקי עשיתי ככל אשר צויתני', "I did not eat of it when grieving, nor did I devour it when ritually defiled, nor did I make use of it for the dead; I have heeded the voice of Adonoy, my God; I have fulfilled everything that You commanded me.");
insert into verses values (6,   5, 28, 9,  'יקימך יקוק לו לעם קדוש כאשר נשבע-לך כי תשמר את-מצות יקוק אלקיך והלכת בדרכיו', "Adonoy will establish you for Himself as a holy people as He swore to you; when you will guard the commandments of Adonoy, your God, and will go in His ways.");
insert into verses values (13,  5, 31, 12, 'הקהל את-העם האנשים והנשים והטף וגרך אשר בשעריך למען ישמעו ולמען ילמדו ויראו את-יקוק אלקיכם ושמרו לעשות אל-כל-דברי התורה הזאת', "Assemble the people—the men and the women and the infants, and your convert who is in your towns; in order for them to hear and in order for them to learn to fear Adonoy, your God, and they will make sure to fulfill all the statements of this Torah.");
insert into verses values (14,  5, 31, 19, 'ועתה כתבו לכם את-השירה הזאת ולמדה את-בני-ישראל שימה בפיהם למען תהיה-לי השירה הזאת לעד בבני ישראל', "And now write for yourselves this song and teach it to Bnei Yisroel to place it in their mouths; in order that this song will be for Me a witness against Bnei Yisroel.");
insert into verses values (483, 5, 32, 3,  'כי שם יקוק אקרא הבו גדל לאלקינו', "When I pronounce the name of Adonoy, give greatness to our God,");
insert into verses values (367, 5, 32, 38, 'אשר חלב זבחימו יאכלו ישתו יין נסכיכם', "They ate the fat of their sacrifices, they drank the wine of their libation? Let them arise and help you, let them serve as your shield.");
