insert into verses values (64,  3, 4,  13, 'ואם כל-עדת ישראל ישגו ונעלם דבר מעיני הקהל ועשו אחת מכל-מצות יקוק אשר לא-תעשינה ואשמו', null);
insert into verses values (65,  3, 5,  1,  'ונפש כי-תחטא ושמעה קול אלה והוא עד או ראה או ידע אם-לוא יגיד ונשא עונו', null);
insert into verses values (311, 3, 5,  8,  'והביא אתם אל-הכהן והקריב את-אשר לחטאת ראשונה ומלק את-ראשו ממול ערפו ולא יבדיל', null);
insert into verses values (68,  3, 5,  11, 'ואם-לא תשיג ידו לשתי תרים או לשני בני-יונה והביא את-קרבנו אשר חטא עשירת האפה סלת לחטאת לא-ישים עליה שמן ולא-יתן עליה לבנה כי חטאת היא', null);
insert into verses values (67,  3, 5,  15, 'נפש כי-תמעל מעל וחטאה בשגגה מקדשי יקוק והביא את-אשמו ליקוק איל תמים מן-הצאן בערכך כסף-שקלים בשקל-הקדש לאשם', null);
insert into verses values (66,  3, 5,  17, 'ואם-נפש כי תחטא ועשתה אחת מכל-מצות יקוק אשר לא תעשינה ולא-ידע ואשם ונשא עונו', null);
