insert into verses values (27,  3, 6,  3,  'ולבש הכהן מדו בד ומכנסי-בד ילבש על-בשרו והרים את-הדשן אשר תאכל האש את-העלה על-המזבח ושמו אצל המזבח', null);
insert into verses values (481, 3, 6,  5,  'והאש על-המזבח תוקד-בו לא תכבה ובער עליה הכהן עצים בבקר בבקר וערך עליה העלה והקטיר עליה חלבי השלמים', null);
