insert into verses values (187, 5, 24, 15, 'ביומו תתן שכרו ולא-תבוא עליו השמש כי עני הוא ואליו הוא נשא את-נפשו ולא יקרא עליך אל-יקוק והיה בך חטא', null);
insert into verses values (426, 5, 24, 16, 'לא-יומתו אבות על-בנים ובנים לא-יומתו על-אבות איש בחטאו יומתו', null);
insert into verses values (398, 5, 24, 17, 'לא תטה משפט גר יתום ולא תחבל בגד אלמנה', null);
insert into verses values (115, 5, 24, 19, 'כי תקצר קצירך בשדך ושכחת עמר בשדה לא תשוב לקחתו לגר ליתום ולאלמנה יהיה למען יברכך יקוק אלקיך בכל מעשה ידיך', null);
insert into verses values (210, 5, 25, 2,  'והיה אם-בן הכות הרשע והפילו השפט והכהו לפניו כדי רשעתו במספר', null);
insert into verses values (436, 5, 25, 3,  'ארבעים יכנו לא יסיף פן-יסיף להכתו על-אלה מכה רבה ונקלה אחיך לעיניך', null);
insert into verses values (382, 5, 25, 4,  'לא-תחסם שור בדישו', null);
insert into verses values (203, 5, 25, 5,  'כי-ישבו אחים יחדו ומת אחד מהם ובן אין-לו לא-תהיה אשר-המת החוצה לאיש זר יבמה יבא עליה ולקחה לו לאשה ויבמה', null);
insert into verses values (204, 5, 25, 9,  'ונגשה יבמתו אליו לעיני הזקנים וחלצה נעלו מעל רדלו וירקה בפניו וענתה ואמרה ככה יעשה לאיש אשר לא-יבנה את-בית אחיו', null);
insert into verses values (232, 5, 25, 12, 'וקצתה את-כפה לא תחוס עינך', null);
insert into verses values (419, 5, 25, 14, 'לא-יהיה לך בביתך איפה ואיפה גדולה וקטנה', null);
insert into verses values (176, 5, 25, 17, 'זכור את אשר-עשה לך עמלק בדרך בצאתכם ממצרים', null);
insert into verses values (175, 5, 25, 19, 'והיה בהניח יקוק אלקיך לך מכל-איביך מסביב בארץ אשר יקוק-אלקיך נתן לך נחלה לרשתה תמחה את-זכר עמלק מתחת השמים לא תשכח', null);
insert into verses values (123, 5, 26, 5,  'וענית ואמרת לפני יקוק אלקיך ארמי אבד אבי וירד מצרימה ויגר שם במתי מעט ויהי-שם לגוי עצום ורב', null);
insert into verses values (485, 5, 26, 12, 'כי תכלה לעשר את-כל-מעשר תבואתך בשנה השלישת שנת המעשר ונתתה ללוי לגר ליתום ולאלמנה ואכלו בשעריך ושבעו', null);
insert into verses values (122, 5, 26, 13, 'ואמרת לפני יקוק אלקיך בערתי הקדש מן-הבית וגם נתתיו ללוי ולגר ליתום ולאלמנה ככל-מצותך אשר צויתני לא-עברתי ממצותיך ולא שכחתי', null);
insert into verses values (334, 5, 26, 14, 'לא-אכלתי באני ממנו ולא-בערתי ממנו בטמא ולא-נתתי ממנו למת שמעתי בקול יקוק אלקי עשיתי ככל אשר צויתני', null);
insert into verses values (6,   5, 28, 9,  'יקימך יקוק לו לעם קדוש כאשר נשבע-לך כי תשמר את-מצות יקוק אלקיך והלכת בדרכיו', null);
insert into verses values (13,  5, 31, 12, 'הקהל את-העם האנשים והנשים והטף וגרך אשר בשעריך למען ישמעו ולמען ילמדו ויראו את-יקוק אלקיכם ושמרו לעשות אל-כל-דברי התורה הזאת', null);
insert into verses values (14,  5, 31, 19, 'ועתה כתבו לכם את-השירה הזאת ולמדה את-בני-ישראל שימה בפיהם למען תהיה-לי השירה הזאת לעד בבני ישראל', null);
insert into verses values (483, 5, 32, 3,  'כי שם יקוק אקרא הבו גדל לאלקינו', null);
insert into verses values (367, 5, 32, 38, 'אשר חלב זבחימו יאכלו ישתו יין נסכיכם', null);