.echo OFF

drop table if exists rambam;
drop table if exists rambamNotes;

create table rambam (
	_id integer NOT NULL primary key,
	mitzvahId integer NOT NULL UNIQUE,
	mitzvahNumber integer NOT NULL,
	source integer NOT NULL,
	type char(1) NOT NULL,	-- D(eos), P(eulah), M(iddos), (dibbu)R
	whoApplies char(1) NOT NULL,	-- A(ll), M(en), W(omen), Y(isroel), (Y)I(sroel Men), L(eviim), K(ohanim), (Kohen Ga)D(ol), (kin)G, B(eis Din), Z(arim)
	originalText varchar,
	foreign key (mitzvahId) references mitzvos(_id) on delete cascade,
	foreign key (source) references verses(_id) on delete cascade
);


create table rambamNotes (
	_id integer NOT NULL primary key,
	hebrewNote varChar,
	enNote varChar,
	foreign key(_id) references rambam(_id) on delete cascade
);

